INSERT INTO `tb_diemden` (`id`, `id_mien`, `name`, `img`, `gioithieu`, `slug`) VALUES
(0, 3, 'jhfhgjgj', 'jhfhgjgj.1577891731.jpg', 'kjhkjhkh', 'diem-den-jhfhgjgj.html'),
(1, 1, 'Hà Nội', 'ha_noi.jpg', '', ''),
(2, 1, 'Hạ Long', 'ha_long.jpg', 'Vịnh Hạ Long nơi rồng đáp xuống, là danh thắng quốc gia được xếp hạng từ năm 1962. Hạ Long có 1.969 hòn đảo, lô nhô trên mặt biển, nổi tiếng nhất là các hòn Lư Hương, Gà Chọi, Cánh Buồm, Mâm Xôi, đảo Ngọc Vừng, Ti Tốp, Tuần Châu. Hạ Long như bức tranh thủy mặc khổng lồ, tuyệt đẹp, xứng đáng là biểu tượng du lịch Việt Nam.', ''),
(3, 1, 'Ninh Bình', 'ninh_binh.jpg', '', ''),
(4, 1, 'Lào Cai', 'lao-cai.1577891907.jpg', 'Lào Cai là khu du lịch trọng tâm của miền Bắc với những thắng cảnh Sa Pa thị trấn trong mây, chợ tình quyến rũ, đỉnh Phan Xi Păng và Cửa khẩu Quốc tế Lào Cai Hà Khẩu. Lào Cai có nhiều địa danh lịch sử, hang động tự nhiên, đặc sản và là nơi mang đậm nét đặc trưng văn hoá độc đáo của nhiều dân tộc anh em.', 'lao-cai.html'),
(5, 2, 'Thanh Hóa', 'thanh-hoa.1577891990.jpg', 'Thanh Hóa Là 1 địa điểm nổi tiếng về du lịch ví dụ như suối cá thần,biển sầm sơn', 'diem-den-thanh-hoa.html'),
(6, 2, 'Quảng Bình', 'quang-binh.1577892033.jpg', 'Quảng Bình Thật Là Đẹp', 'diem-den-quang-binh.html'),
(7, 2, 'Đà Lạt', 'da_lat.jpg', 'Đà Lạt mộng mơ nơi mimosa và ngàn hoa khoe sắc, từ đồi Robin ngắm Hồ Tuyền Lâm, núi Voi, viếng Thiền Viện Trúc Lâm, thăm Dinh Bảo Đại, tản bộ dưới những tán thông, ngắm biệt thự cổ, nhấm nháp ly café ấm áp trong thời tiết se lạnh. Những chuyến xe ngựa thổ mộ chạy quanh Hồ Xuân Hương cũng là nét duyên của Đà Lạt mờ sương. ', ''),
(8, 2, 'Đà Nẵng', 'da_nang.jpg', '', ''),
(9, 2, 'Huế', 'hue.jpg', 'Huế thương sơn thủy hữu tình, nơi tọa lạc Đại Nội, Ngọ Môn, Điện Thái Hòa, Tử Cấm Thành, Lăng Tự Đức, Thế Miếu, Hiển Lâm Các, Cửu Đỉnh xưa của 13 vị vua triều Nguyễn. Chùa Thiên Mụ lưu giữ cổ vật lịch sử, nghệ thuật quý giá. Chiều buông, ta ngồi thuyền rồng xuôi dòng sông Hương thưởng thức ca hò Huế, thả hoa đăng cầu phúc lộc.', ''),
(10, 3, 'Phú Quốc', 'phu-quoc.1577892105.jpg', 'Đảo Phú Quốc Thật Là Đẹp', 'diem-den-phu-quoc.html'),
(11, 3, 'Vũng Tàu', 'vung-tau.1577892135.jpg', 'Du lich Vũng Tàu: Những cung đường biển đẹp như mơ, ngọn Hải đăng cổ nổi tiếng, tượng Chúa giang tay bình yên, những góc phố thơ mộng, cùng những món ăn hấp dẫn là những gì du khách không thể bỏ qua khi đến với Vũng Tàu. Vũng Tàu trở thành đô thị loại I năm 2013, là một thành phố đáng tới, đáng sống và hạnh phúc.', 'diem-den-vung-tau.html'),
(12, 3, 'Hồ Chí Minh', 'ho-chi-minh.1577892195.jpg', 'Hồ Chí Mình Thành Phố Của Những Con Người Ngày Đêm Không Ngủ', 'diem-den-ho-chi-minh.html'),
(13, 3, 'Cần Thơ', 'can-tho.1577892219.png', 'Về Cần Thơ thăm Bến Ninh Kiều, ngắm cầu Quang Trung, dạo chợ nổi Cái Răng nhộn nhịp, tham quan Nhà Cổ, thưởng thức đặc sản trái cây miền Tây trĩu ngọt. Khu du lịch sinh thái Mỹ Khánh là nơi bạn trải nghiệm các trò chơi dân gian vui nhộn như câu cá, chèo thuyền trên sông, đua heo.', 'diem-den-can-tho.html'),
(14, 1, 'Hà Giang', 'ha-giang.1577892273.jpg', 'Hà Giang - mảnh đất địa đầu Tổ quốc Việt Nam với khí hậu quanh năm mát mẻ, không khí trong lành là địa chỉ du lịch hấp dẫn đối với du khách. Đến Hà Giang, các bạn sẽ được chiêm ngưỡng nhiều thắng cảnh hùng vĩ, ngoạn mục như cao nguyên Đồng Văn, núi đôi Quản Bạ, dinh vua Mèo, đèo Mã Pí Lèng, cột cờ Lũng Cú, cùng nhiều ghềnh thác, hang động kỳ thú sẽ mang lại cho bạn trải nghiệm tuyệt vời', 'diem-den-ha-giang.html'),
(15, 2, 'Nha Trang', 'nha-trang.1577892299.jpg', 'Biển Nha Trang tuyệt vời với Vinpearl Nha Trang 5* sang trọng, hòn Mun Hòn Tằm nước trong veo và san hô lộng lẫy, cùng với vịnh Ninh Vân, vịnh Vân Phong hoang sơ và thuần khiết. Viện Hải dương học Nha Trang có trên 20.000 mẫu sinh vật dưới nước, tháp Bà Ponagar hoàn mỹ của người Chăm, cảng Vũng Rô, làng Đại Lãnh và chợ Đầm nhộn nhịp.', 'diem-den-nha-trang.html');

