-- Đang đổ dữ liệu cho bảng `tb_news`
--

INSERT INTO `tb_news` (`id`, `tieude`, `tomtat`, `noidung`, `ngaydang`, `id_user`, `id_diemden`, `avatar`, `slug`) VALUES
(1, 'Bí kíp ăn chơi ở Đà Lạt 3 ngày 2 đêm cho khỏi phí thanh xuân', 'Đà Lạt không chỉ có hoa dã quỳ mùa thu, hoa mai anh đào mùa xuân, hoa phượng tím mùa hè… mà còn có những cảnh đẹp ấn tượng hay những quán cà phê lãng mạn, đậm chất Tây Nguyên.', '<p><img alt=\"Du lịch đà lạt 3 ngày 2 đêm\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-1.jpg\" style=\"height:960px; width:803px\" /></p>\r\n\r\n<p><em>Đ&agrave; Lạt - Chuyến đi &yacute; nghĩa của thời thanh xu&acirc;n.</em></p>\r\n\r\n<h2><a id=\"type1\" name=\"type1\"><strong>Di chuyển</strong></a></h2>\r\n\r\n<p>Xe Th&agrave;nh Bưởi l&agrave; phương tiện di chuyển l&ecirc;n Đ&agrave; Lạt được nhiều người lựa chọn nhất, tr&ecirc;n xe c&ograve;n phục vụ bữa ăn nhẹ m&agrave; lại c&oacute; gi&aacute; cũng hấp dẫn, chỉ c&oacute; 220.000Đ/lượt. Xe chất lượng, giường nằm thoải m&aacute;i lại c&ograve;n c&oacute; xe trung chuyển, đảm bảo đi l&agrave; h&agrave;i l&ograve;ng.</p>\r\n\r\n<h2><a id=\"type2\" name=\"type2\"><strong>Đến Đ&agrave; Lạt n&ecirc;n ở đ&acirc;u?</strong></a></h2>\r\n\r\n<p>Đi&nbsp;<a href=\"https://www.vietnambooking.com/du-lich/tour-du-lich/du-lich-da-lat.html\"><strong>du lịch Đ&agrave; Lạt</strong></a>&nbsp;nhất định phải ở homestay để trải nghiệm những điều th&uacute; vị, cảm nhận cuộc sống b&igrave;nh y&ecirc;n v&agrave; kh&ocirc;ng gian như đang ở nh&agrave;. Đặc biệt, ở Đ&agrave; Lạt c&ograve;n c&oacute; những homestay si&ecirc;u xinh đảm bảo đứng vị tr&iacute; n&agrave;o chụp ảnh cũng si&ecirc;u đẹp. Lần đi Đ&agrave; Lạt n&agrave;y, m&igrave;nh chọn homestay ở ngay trung t&acirc;m th&agrave;nh phố, v&igrave; đi một m&igrave;nh n&ecirc;n m&igrave;nh chọn ở ph&ograve;ng dorm để tiết chi ph&iacute;. Gi&aacute; ph&ograve;ng mỗi đ&ecirc;m chỉ c&oacute; 100.000Đ, bao gồm cả bữa s&aacute;ng, ph&ograve;ng rộng r&atilde;i tho&aacute;ng m&aacute;t, cũng thoải m&aacute;i lắm. Ph&iacute;a dưới homestay c&ograve;n c&oacute; một qu&aacute;n c&agrave; ph&ecirc; nhỏ xinh, buổi s&aacute;ng bạn c&oacute; thể ngồi thưởng thức bữa s&aacute;ng, nh&acirc;m nhi một ly c&agrave; ph&ecirc; v&agrave; ngắm cảnh th&agrave;nh phố buổi s&aacute;ng sớm.</p>\r\n\r\n<h2><a id=\"type3\" name=\"type3\"><strong>Đến Đ&agrave; Lạt tham quan đi đ&acirc;u để tiết kiệm?</strong></a></h2>\r\n\r\n<h3><strong>1. Những qu&aacute;n c&agrave; ph&ecirc; si&ecirc;u xinh</strong></h3>\r\n\r\n<p><strong>Misty forest:</strong>&nbsp;Qu&aacute;n được decor theo kiểu ch&acirc;u &Acirc;u, g&oacute;c n&agrave;o cũng xinh v&agrave; l&ecirc;n h&igrave;nh đẹp xuất sắc. Nước uống của qu&aacute;n cũng ngon v&agrave; gi&aacute; cả cũng ổn lắm, chỉ từ 25.000Đ &ndash; 40.000Đ, ngo&agrave;i nước uống, qu&aacute;n c&ograve;n c&oacute; b&aacute;n đồ lưu niệm.</p>\r\n\r\n<p>+ Địa chỉ: 124 Bạch Đằng, P. 7, Đ&agrave; Lạt</p>\r\n\r\n<p><strong>M&ecirc; Linh coffe:</strong>&nbsp;Qu&aacute;n n&agrave;y kh&aacute; quen thuộc với kh&aacute;ch du lịch Đ&agrave; Lạt, m&igrave;nh cũng đặc biệt th&iacute;ch qu&aacute;n n&agrave;y, bởi kh&ocirc;ng gian qu&aacute;n đ&uacute;ng kiểu h&ograve;a m&igrave;nh với thi&ecirc;n nhi&ecirc;n, gi&oacute; m&aacute;t lồng lộng, view tự nhi&ecirc;n. Qu&aacute;n c&ograve;n c&oacute; mấy anh chơi nhạc cụ d&acirc;n tộc, vừa ngắm cảnh vừa ng&acirc;m nhi một ly c&agrave; ph&ecirc; cao nguy&ecirc;n vừa nghe nhạc d&acirc;n tộc th&igrave; đ&uacute;ng l&agrave; tuyệt vời. Gần qu&aacute;n c&agrave; ph&ecirc; n&agrave;y cũng c&oacute; nhiều c&aacute;nh đồng hoa cẩm t&uacute; cầu, hoa hướng dương, tam gi&aacute;c mạch, tr&ecirc;n đường đến đ&acirc;y, bạn c&oacute; thể tha hồ chụp ảnh, ngắm cảnh.</p>\r\n\r\n<p>+ Địa chỉ: Tổ 20, th&ocirc;n 4, x&atilde; T&agrave; Nung, Tp. Đ&agrave; Lạt</p>\r\n\r\n<p><strong>Up coffee:</strong>&nbsp;Qu&aacute;n nằm ở khu vực trung t&acirc;m th&agrave;nh phố ng&agrave;n hoa, nước uống cũng tạm được, từ vị tr&iacute; của qu&aacute;n bạn c&oacute; thể chi&ecirc;m ngưỡng đồi n&uacute;i v&agrave; khung cảnh thi&ecirc;n nhi&ecirc;n Đ&agrave; Lạt.</p>\r\n\r\n<p>+ Địa chỉ: 06 Triệu Việt Vương, P. 4, Tp. Đ&agrave; Lạt</p>\r\n\r\n<p>+ Thực đơn: từ 27.000Đ &ndash; 49.000Đ</p>\r\n\r\n<p><strong>Still coffee:</strong>&nbsp;Qu&aacute;n được thiết kế ấn tượng, đẹp mắt, với m&igrave;nh, qu&aacute;n n&agrave;y vị tr&iacute; n&agrave;o cũng đẹp v&agrave; l&ecirc;n h&igrave;nh cũng ấn tượng. Ch&iacute;nh v&igrave; thế m&agrave; qu&aacute;n l&uacute;c n&agrave;o cũng đ&ocirc;ng, nước uống cũng ổn, đặc biệt qu&aacute;n c&ograve;n c&oacute; mấy ch&uacute; ch&oacute; cực đ&aacute;ng y&ecirc;u. Bạn c&oacute; thể tranh thủ tạo d&aacute;ng với mấy ch&uacute; ch&oacute; của qu&aacute;n nữa nh&eacute;!</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-2.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Still Coffee - Đảm bảo chụp g&oacute;c n&agrave;o cũng đẹp.</em></p>\r\n\r\n<p>+ Địa chỉ: 59 Nguyễn Tr&atilde;i, Tp. Đ&agrave; Lạt</p>\r\n\r\n<p>+ Thực đơn: 20.000Đ &ndash; 47.000Đ</p>\r\n\r\n<p><strong>Qu&aacute;n của thời thanh xu&acirc;n:</strong>&nbsp;Qu&aacute;n n&agrave;y hiện đang kh&aacute; &ldquo;hot&rdquo;, qu&aacute;n được thiết kế đơn giản nhưng tinh tế v&agrave; ấn tượng, đặc biệt kh&ocirc;ng gian y&ecirc;n b&igrave;nh lạ l&ugrave;ng khiến m&igrave;nh lu&ocirc;n cảm thấy th&iacute;ch th&uacute;. Ph&iacute;a trước đường v&agrave;o qu&aacute;n c&oacute; nhiều c&acirc;y hồng Đ&agrave; Lạt tr&aacute;i sum su&ecirc;, tr&ecirc;n đường đến qu&aacute;n, bạn c&oacute; thể dừng ch&acirc;n chụp ảnh thỏa th&iacute;ch. Qu&aacute;n kh&ocirc;ng b&aacute;n nước theo thực đơn m&agrave; chỉ phục vụ tr&agrave; v&agrave; bạn c&oacute; thể trả tiền theo sự h&agrave;i l&ograve;ng của m&igrave;nh. Ở đ&acirc;y c&ograve;n c&oacute; mấy ch&uacute; m&egrave;o b&eacute;o b&eacute;o dễ thương như cục b&ocirc;ng, ai th&iacute;ch m&egrave;o đến qu&aacute;n th&igrave; tha hồ m&agrave; nựng nịu.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-5.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-4.jpg\" style=\"height:960px; width:782px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-3.jpg\" style=\"height:960px; width:871px\" /><em>Qu&aacute;n của Thời Thanh Xu&acirc;n.</em></p>\r\n\r\n<p>+ Địa chỉ: 9 Triệu Việt Vương. P. 4, Tp. Đà Lạt</p>\r\n\r\n<p><strong>A</strong><strong>nh v&agrave; em:</strong>&nbsp;Theo m&igrave;nh, qu&aacute;n n&agrave;y uống v&agrave;o buổi tối l&agrave; tuyệt nhất, chọn một g&oacute;c th&iacute;ch hợp của qu&aacute;n, bạn tha hồ ngắm th&agrave;nh phố về đ&ecirc;m, lung linh v&ocirc; c&ugrave;ng.</p>\r\n\r\n<p>+ Địa chỉ: 285 B&ugrave;i Thị Xu&acirc;n, P. 8 Tp. Đ&agrave; Lạt</p>\r\n\r\n<h3><strong>2.&nbsp;</strong><strong>Đi chơi ở đ&acirc;u</strong><strong>?</strong></h3>\r\n\r\n<p>- Khu du lịch L&aacute; Phong l&agrave; điểm đến mới được đưa v&agrave;o phục vụ du kh&aacute;ch nhưng để lại ấn tượng với du kh&aacute;ch. Những c&ocirc;ng tr&igrave;nh đi&ecirc;u khắc, những con đường hoa, hồ c&aacute;&hellip; ở đ&acirc;y thật sự l&agrave; một kh&ocirc;ng gian ho&agrave;n hảo d&agrave;nh cho du kh&aacute;ch. Đặc biệt, ở đ&acirc;y c&ograve;n c&oacute; một hostel dưới l&ograve;ng đất v&ocirc; c&ugrave;ng ấn tượng, ngo&agrave;i ra bạn cũng c&oacute; thể cắm trại trong khu&ocirc;n vi&ecirc;n của khu du lịch.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-6.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-19.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Khu du lịch L&aacute; Phong đảm bảo l&ecirc;n h&igrave;nh &quot;si&ecirc;u ảo&quot;.</em></p>\r\n\r\n<p>+ Địa chỉ: 45 Đặng Th&aacute;i Th&acirc;n, P. 3, Tp. Đ&agrave; Lạt</p>\r\n\r\n<p>+ Gi&aacute; v&eacute;: v&eacute; 50.000Đ/người, 35.000Đ/trẻ em</p>\r\n\r\n<p>- Hoa sơn điền trang để lại ấn tượng với du kh&aacute;ch nhờ c&oacute; b&agrave;n tay khổng lồ giữa n&uacute;i rừng, kh&ocirc;ng gian trong l&agrave;nh, m&aacute;t mẻ, Hoa sơn điền trang lu&ocirc;n hấp dẫn du kh&aacute;ch khắp nơi khi đến với Đ&agrave; Lạt.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-7.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-17.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>B&agrave;n tay khổng lồ ở Hoa Sơn Điền Trang.</em></p>\r\n\r\n<p>+ Địa chỉ: Tiểu khu 159B, P. 5, Tp. Đ&agrave; Lạt</p>\r\n\r\n<p>+ Gi&aacute; v&eacute;: 50.000Đ/người</p>\r\n\r\n<p>- Hồ Tuyền L&acirc;m l&agrave; hồ nước ngọt rộng nhất ở Đ&agrave; Lạt v&agrave; cũng l&agrave; điểm tham quan chụp ảnh l&iacute; tưởng nhất ở th&agrave;nh phố ng&agrave;n hoa. Kh&ocirc;ng gian y&ecirc;n tĩnh, tho&aacute;ng m&aacute;t, trong l&agrave;nh, hoang sơ của hồ Tuyền L&acirc;m mang đến những trải nghiệm kh&oacute; qu&ecirc;n d&agrave;nh cho du kh&aacute;ch.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-8.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-15.jpg\" style=\"height:960px; width:960px\" /><em>Đến hồ Tuyền L&acirc;m cảm nhận sự b&igrave;nh y&ecirc;n giữa n&uacute;i rừng.</em></p>\r\n\r\n<p>+ Địa chỉ: dưới ch&acirc;n n&uacute;i Phượng Ho&agrave;ng</p>\r\n\r\n<p>+ Gi&aacute; v&eacute;: Miễn ph&iacute;</p>\r\n\r\n<p>- Happy Hill l&agrave; một phim trường mới nổi nằm trong khu vực hồ Tuyền L&acirc;m. Thời gian gần đ&acirc;y, Happy Hill g&acirc;y ấn tượng trong l&ograve;ng du kh&aacute;ch bởi kh&ocirc;ng gian gần gũi với thi&ecirc;n nhi&ecirc;n, thiết kế ấn tượng, bạn c&oacute; thể đến đ&acirc;y để cảm nhận thi&ecirc;n nhi&ecirc;n của Đ&agrave; Lạt v&agrave; tha hồ chụp ảnh.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-9.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-10.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Phim trường Happy Hill.</em></p>\r\n\r\n<p>+ Địa chỉ: Khu du lịch sinh th&aacute;i, nh&agrave; h&agrave;ng Đ&aacute; Ti&ecirc;n, hồ Tuyền L&acirc;m, Tp. Đ&agrave; Lạt</p>\r\n\r\n<p>+ Gi&aacute; v&eacute;: 30.000Đ</p>\r\n\r\n<p>- Ch&ugrave;a Linh Phước: Ng&ocirc;i ch&ugrave;a nổi tiếng với những c&ocirc;ng tr&igrave;nh được l&agrave;m bằng c&aacute;c mảnh ve chai v&agrave; cũng l&agrave; ng&ocirc;i ch&ugrave;a giữ nhiều kỷ lục ở Việt Nam. Đến với ch&ugrave;a Linh Phước bạn kh&ocirc;ng chỉ được viếng Phật, cầu an m&agrave; c&ograve;n c&oacute; thể t&igrave;m được v&ocirc; v&agrave;n g&oacute;c ảnh đẹp.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-11.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Ch&ugrave;a Linh Phước.</em></p>\r\n\r\n<p>+ Địa chỉ: 120 Tự Phước, P. 11, Tp. Đ&agrave; Lạt&nbsp;</p>\r\n\r\n<p>+ Gi&aacute; v&eacute;: Miễn ph&iacute;</p>\r\n\r\n<p>- Đồi ch&egrave; cầu đất: Kh&ocirc;ng gian xanh mướt, rộng lớn của đồi ch&egrave; Cầu Đất đảm bảo sẽ mang đến cho bạn kh&ocirc;ng &iacute;t ảnh đẹp. Tham quan, chụp ảnh ở đồi ch&egrave; xong, bạn cũng c&oacute; thể dừng ch&acirc;n ở&nbsp; một qu&aacute;n nước gần đồi ch&egrave;, thưởng thức ly ch&egrave; xanh n&oacute;ng hổi hoặc thưởng thức một ly c&agrave; ph&ecirc; đậm đ&agrave;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-12.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Đồi ch&egrave; Cầu Đất.</em></p>\r\n\r\n<p>+ Địa chỉ: th&ocirc;n Cầu Đất, x&atilde; Xu&acirc;n Trường, Tp. Đ&agrave; Lạt</p>\r\n\r\n<p>+ Gi&aacute; v&eacute;: Miễn ph&iacute;</p>\r\n\r\n<p>- Hồ Xu&acirc;n Hương: Đ&atilde; đến Đ&agrave; Lạt m&agrave; kh&ocirc;ng đạp xe một v&ograve;ng hồ Xu&acirc;n Hương th&igrave; thật l&agrave; tiếc, với m&igrave;nh, ngắm ho&agrave;ng h&ocirc;n ở đ&acirc;y rất tuyệt. Thời tiết se lạnh, thả hồn theo gi&oacute;, cảm nhận ch&uacute;t b&igrave;nh y&ecirc;n giữa l&ograve;ng Đ&agrave; Lạt v&agrave; lắng nghe tiếng chim h&oacute;t đ&uacute;ng l&agrave; kh&ocirc;ng g&igrave; tuyệt bằng. Bạn c&oacute; thể thu&ecirc; xe đạp v&agrave; dạo một v&ograve;ng hồ, vừa đạp xe, vừa ngắm cảnh hồ vừa cảm nhận cuộc sống thường nhật của người Đ&agrave; Lạt.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-13.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Hồ Xu&acirc;n Hương.</em></p>\r\n\r\n<p>+ Gi&aacute; thu&ecirc; xe đạp: 50.000Đ/chiếc đi được 2 tiếng.</p>\r\n\r\n<p>- Quảng trường L&acirc;m Vi&ecirc;n: Nằm ngay s&aacute;t hồ Xu&acirc;n Hương, quảng trường L&acirc;m Vi&ecirc;n l&agrave; một điểm check-in quen thuộc của kh&aacute;ch du lịch Đ&agrave; Lạt.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-18.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Quảng trường L&acirc;m Vi&ecirc;n - Điểm check-in quen thuộc ở Đ&agrave; Lạt.</em></p>\r\n\r\n<h2><a id=\"type4\" name=\"type4\"><strong>Ăn g&igrave;?</strong></a></h2>\r\n\r\n<p>Ẩm thực Đ&agrave; Lạt v&ocirc; c&ugrave;ng phong ph&uacute;, m&agrave; m&oacute;n n&agrave;o cũng hấp dẫn, gi&aacute; cả lại rất ok, m&igrave;nh sẽ gợi &yacute; cho bạn một v&agrave;i qu&aacute;n, th&agrave; hồ m&agrave; lựa chọn nh&eacute;!</p>\r\n\r\n<p>- Tiệm b&aacute;nh cối xay gi&oacute;: B&aacute;nh m&igrave; ở đ&acirc;y kh&aacute; rẻ, hơn nữa c&ograve;n c&oacute; b&aacute;nh ngọt v&agrave; nhiều loại b&aacute;nh cũng hấp dẫn lắm.</p>\r\n\r\n<p>+ Địa chỉ: Số 1, Khu H&ograve;a B&igrave;nh, Tp. Đ&agrave; Lạt</p>\r\n\r\n<p>+ Gi&aacute;: 15.000Đ &ndash; 39.000Đ</p>\r\n\r\n<p>- B&aacute;nh căn: Ở Đ&agrave; Lạt c&oacute; nhiều qu&aacute;n b&aacute;nh căn, t&ugrave;y theo khẩu vị m&agrave; bạn c&oacute; thể chọn m&oacute;n n&agrave;o t&ugrave;y th&iacute;ch, nhưng m&igrave;nh thấy qu&aacute;n b&aacute;nh căn tr&ecirc;n đường Paster l&agrave; ngon nhất.</p>\r\n\r\n<p>- Nem nướng, ri&ecirc;ng m&oacute;n n&agrave;y th&igrave; qu&aacute;n B&agrave; H&ugrave;ng l&agrave; nổi tiếng nhất.</p>\r\n\r\n<p>+ Địa chỉ: 328 Phan Đ&igrave;nh Ph&ugrave;ng, Tp. Đ&agrave; Lạt</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-14.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Nem nướng B&agrave; H&ugrave;ng.</em></p>\r\n\r\n<p>+ Gi&aacute;: 30.000Đ &ndash; 66.000Đ/phần&nbsp;</p>\r\n\r\n<p>- Lẩu g&agrave; l&aacute; &eacute;, một phần lẩu chỉ c&oacute; 200.000Đ l&agrave; đủ cho 3 người ăn no căng bụng rồi.</p>\r\n\r\n<p>+ Địa chỉ: 99 Ph&ugrave; Đổng Thi&ecirc;n Vương, P. 8, Tp. Đ&agrave; Lạt</p>\r\n\r\n<p>- B&aacute;nh tr&aacute;ng nướng l&agrave; một trong những m&oacute;n ăn vặt kh&ocirc;ng thể n&agrave;o bỏ qua khi dạo chơi th&agrave;nh phố ng&agrave;n hoa, mỗi b&aacute;nh chỉ từ 15.000Đ &ndash; 25.000Đ nhưng v&ocirc; c&ugrave;ng chất lượng, b&aacute;nh n&oacute;ng hổi ăn giữa thời tiết se lạnh l&agrave; tuyệt nhất.</p>\r\n\r\n<p>+ Địa chỉ: rất nhiều nơi ở Đ&agrave; Lạt c&oacute; b&aacute;n m&oacute;n n&agrave;y, nhiều nhất l&agrave; ở khu vực chợ đ&ecirc;m.</p>\r\n\r\n<p>- Kem bơ Thanh Thảo cũng l&agrave; một trong những m&oacute;n ăn độc đ&aacute;o m&agrave; nhất định phải thưởng thức nếu c&oacute; dịp&nbsp;<strong><a href=\"https://www.vietnambooking.com/du-lich/tour-du-lich/du-lich-da-lat.html\">đi&nbsp;Đ&agrave; Lạt</a></strong>. Vị kem b&eacute;o ngậy, lạnh lạnh thật tuyệt vời.</p>\r\n\r\n<p>+ Địa chỉ: 76 Nguyễn Văn Trỗi, P. 2, Tp. Đ&agrave; Lạt</p>\r\n\r\n<p>+ Gi&aacute;: 20.000Đ/ly</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/bikip-an-choi-odalat-3n2d-cho-khoi-phi-thanh-xuan-08112018-16.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Đến Đ&agrave; Lạt kh&ocirc;ng chỉ được ngắm cảnh đẹp m&agrave; c&ograve;n cảm nhận được sự b&igrave;nh y&ecirc;n của cuộc sống.</em></p>\r\n\r\n<p>Với lịch tr&igrave;nh&nbsp;<a href=\"https://www.vietnambooking.com/du-lich/du-lich-da-lat-3-ngay-gia-tot-khoi-hanh-tu-tp-hcm.html\"><strong><em>du lịch Đ&agrave; Lạt 3 ng&agrave;y 2 đ&ecirc;m</em></strong></a>&nbsp;kh&aacute;m ph&aacute; th&agrave;nh phố ng&agrave;n hoa, dạo chơi những điểm tham quan nhưng m&igrave;nh th&igrave; chi ph&iacute; chỉ khoảng 1.500.000Đ l&agrave; thoải m&aacute;i, v&igrave; hầu hết những điểm tham quan đều miễn ph&iacute;. Chi ph&iacute; n&agrave;y cũng phụ thuộc v&agrave;o việc ăn uống của bạn nữa nh&eacute;!</p>', '2019-12-19 09:10:14', 1, 7, 'bi-kip-an-choi-o-da-lat-3-ngay-2-dem-cho-khoi-phi-thanh-xuan.1576746614.jpg', 'bi-kip-an-choi-o-da-lat-3-ngay-2-dem-cho-khoi-phi-thanh-xuan.146.html'),
(2, 'Tuổi trẻ trọn vẹn phải đến Sapa ít nhất một lần', 'Sapa không còn xa lạ gì với mình nhưng vẫn cứ muốn đi và vẫn không thể nào quên được không khí, cảnh đẹp thiên nhiên ở đó. Và với mình, Sapa vẫn ấm áp vì được đi cùng người thân và được đến nơi mình thích.', '<p>V&igrave; kh&ocirc;ng c&oacute; nhiều chi ph&iacute; n&ecirc;n bọn m&igrave;nh quyết định chỉ đi Sapa 2 ng&agrave;y v&agrave; chỉ tham quan khu vực trung t&acirc;m, bản C&aacute;t C&aacute;t chứ kh&ocirc;ng đi Fansipan nữa. D&ugrave; tham quan &iacute;t nhưng chuyến đi vẫn trọn vẹn v&agrave; &yacute; nghĩa d&agrave;nh cho 2 c&ocirc; n&agrave;ng &ldquo;b&aacute;nh b&egrave;o&rdquo; ch&iacute;nh hiệu.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-24.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<h2><strong>Phương tiện di chuyển</strong></h2>\r\n\r\n<p>M&igrave;nh đặt xe giường nằm Green Bus v&igrave; nghe n&oacute;i xe n&agrave;y an to&agrave;n, giường nằm thoải m&aacute;i lại c&ograve;n c&oacute; bữa ăn nhẹ tr&ecirc;n xe, xe chạy cũng &ecirc;m, tr&ecirc;n đường đi c&oacute; dừng lại 2 trạm, mỗi trạm khoảng 30 ph&uacute;t.</p>\r\n\r\n<h2><strong>L&ecirc;n Sapa n&ecirc;n ở đ&acirc;u?</strong></h2>\r\n\r\n<p>C&oacute; rất nhiều&nbsp;<a href=\"https://www.vietnambooking.com/khach-san/hotels/huyen-sapa.html\">kh&aacute;ch sạn ở Sapa</a>&nbsp;từ sang chảnh đến b&igrave;nh d&acirc;n v&agrave; cũng c&oacute; kh&ocirc;ng &iacute;t homestay khang trang. Lần n&agrave;y m&igrave;nh chọn Hometravel Sapa (Long Anh Hotel). Kh&aacute;ch sạn kh&aacute; lớn, sạch sẽ, phục vụ cũng chu đ&aacute;o, m&igrave;nh thu&ecirc; ph&ograve;ng dorm, 2 giường 1 đ&ecirc;m chỉ c&oacute; 165.000Đ.</p>\r\n\r\n<p>+ Địa chỉ: 242 đường Thạch Sơn, thị trấn Sa Pa, L&agrave;o Cai</p>\r\n\r\n<h2><strong>H&agrave;nh tr&igrave;nh 2 ng&agrave;y 1 đ&ecirc;m kh&aacute;m ph&aacute; Sapa</strong></h2>\r\n\r\n<p>Xe Green Bus xuất ph&aacute;t từ H&agrave; Nội l&uacute;c 22h v&agrave; đến Sapa l&uacute;c 4h s&aacute;ng, nhưng đến 6h kh&aacute;ch mới xuống xe v&igrave; chủ xe cho kh&aacute;ch ngủ lại xe đến s&aacute;ng. Đ&uacute;ng 6h bọn m&igrave;nh xuống xe v&agrave; đi về kh&aacute;ch sạn để ăn s&aacute;ng, vệ sinh c&aacute; nh&acirc;n rồi thu&ecirc; xe m&aacute;y, bắt đầu h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; Sapa.</p>\r\n\r\n<h3><strong>Ng&agrave;y 1: Bản C&aacute;t C&aacute;t &ndash; Haven Coffee</strong></h3>\r\n\r\n<p>Bọn m&igrave;nh thu&ecirc; xe m&aacute;y tại kh&aacute;ch sạn, đi được một đoạn nhưng do đường đi kh&ocirc;ng ổn lắm n&ecirc;n bọn m&igrave;nh quay lại trả xe rồi thu&ecirc; xe &ocirc;m đi cho thoải m&aacute;i. Đến bản C&aacute;t C&aacute;t m&igrave;nh kh&aacute; h&agrave;i l&ograve;ng v&igrave; đ&atilde; an to&agrave;n đến đ&acirc;y m&agrave; kh&ocirc;ng phải l&aacute;i xe, v&eacute; v&agrave;o tham quan l&agrave; 70.000Đ/người. Ở Sapa m&igrave;nh th&iacute;ch bản n&agrave;y nhất v&igrave; kh&ocirc;ng gian rộng, d&acirc;n d&atilde;, đ&uacute;ng chất v&ugrave;ng cao T&acirc;y Bắc, c&oacute; mấy cầu tre v&agrave; cả suối nữa.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-11.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-1.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-2.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-9.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-5.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Bản C&aacute;t C&aacute;t.</em></p>\r\n\r\n<p>Ở bản C&aacute;t C&aacute;t người ta c&ograve;n cho thu&ecirc; đồ d&acirc;n tộc, mỗi bộ l&agrave; 50.000Đ, nhưng v&igrave; biết rằng m&igrave;nh mặc kh&ocirc;ng hợp n&ecirc;n m&igrave;nh quyết định kh&ocirc;ng thu&ecirc; đồ, chỉ dạo chơi xung quanh đảo rồi chụp ảnh c&aacute;c kiểu. Dạo chơi ở bản đến trưa, bọn m&igrave;nh đi bộ đến Haven Coffee v&agrave; Haven Restaurant để ăn trưa v&agrave; chủ yếu l&agrave; ngắm cảnh. Hai khu n&agrave;y t&aacute;ch biệt nhau, nhưng v&igrave; đi bộ hơi mệt n&ecirc;n bọn m&igrave;nh chỉ gh&eacute; Haven Coffee, ở đ&acirc;y cũng c&oacute; đồ ăn, gi&aacute; chỉ từ 80.000Đ &ndash; 90.000Đ, gi&aacute; nước uống từ 50.000Đ &ndash; 80.000Đ. Ngồi chơi ở qu&aacute;n, chụp ảnh, ngắm cảnh đủ kiểu đến 16h m&igrave;nh trở về kh&aacute;ch sạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-18.jpg\" style=\"height:616px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-17.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>Haven Coffee - Đứng g&oacute;c n&agrave;o chụp ảnh cũng đẹp.</em></p>\r\n\r\n<p>Tối đến, bọn m&igrave;nh dạo chơi khu vực trung t&acirc;m thị trấn, v&igrave; đi đ&uacute;ng ng&agrave;y giữa tuần n&ecirc;n ở đ&acirc;y kh&aacute; vắng, kh&ocirc;ng c&oacute; nhiều qu&aacute;n h&agrave;ng rong cũng kh&ocirc;ng c&oacute; nhiều kh&aacute;ch du lịch n&ecirc;n kh&aacute; y&ecirc;n tĩnh. Dạo chơi một v&ograve;ng, tận hưởng kh&ocirc;ng kh&iacute; trong l&agrave;nh, m&aacute;t mẻ bọn m&igrave;nh trở về kh&aacute;ch sạn để nghỉ ngơi.</p>\r\n\r\n<h3><strong>Ng&agrave;y 2: Trung t&acirc;m thị trấn &ndash; Viettrekking</strong></h3>\r\n\r\n<p>Nhờ buổi tối ngủ sớm n&ecirc;n s&aacute;ng h&ocirc;m sau bọn m&igrave;nh dậy kh&aacute; sớm, dạo chơi, chụp ảnh ở khu vực nh&agrave; thờ Đ&aacute;, hồ Sapa, quảng trường, Sapa station&hellip; Buổi s&aacute;ng nắng nhẹ nh&agrave;ng, thời tiết l&iacute; tưởng, chụp ảnh cũng si&ecirc;u đẹp. Viettrekking gần ngay khu vực trung t&acirc;m của thị trấn Sapa n&ecirc;n bọn m&igrave;nh quyết định đi bộ. Viettreking ngo&agrave;i qu&aacute;n c&agrave; ph&ecirc; c&ograve;n c&oacute; homestay, ph&ograve;ng dorm l&agrave; 200.000Đ/người. Chỗ n&agrave;y view cực đẹp, kh&ocirc;ng gian lại tho&aacute;ng m&aacute;t, c&ograve;n c&oacute; thể ngắm được t&agrave;u đi Mường Hoa.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-12.jpg\" style=\"height:788px; width:718px\" /></p>\r\n\r\n<p><em>Sapa Station.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-23.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-16.jpg\" style=\"height:666px; width:960px\" /></p>\r\n\r\n<p><em>Quảng trường trung t&acirc;m thị trấn.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-15.jpg\" style=\"height:720px; width:960px\" /><em>Nh&agrave; thờ Đ&aacute;.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-22.jpg\" style=\"height:720px; width:960px\" /><em>Hồ Sapa.</em></p>\r\n\r\n<p>Ngồi chơi, ngắm cảnh, chụp ảnh ở Viettrekking đến trưa bọn m&igrave;nh trở lại chợ Sapa để ăn trưa, mua &iacute;t đồ rồi về kh&aacute;ch sạn dọn đồ để chuẩn bị trở về H&agrave; Nội.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-6.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-7.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-8.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>Từ vị tr&iacute; của Viettrekking tha hồ ngắm n&uacute;i v&agrave; ruộng bậc thang.</em></p>\r\n\r\n<p>D&ugrave; chuyến đi kh&aacute; ngắn, cũng chỉ đi được những điểm tham quan ở khu vực trung t&acirc;m nhưng cũng đủ để m&igrave;nh trải nghiệm những điều th&uacute; vị, xả stress, h&iacute;t thở kh&ocirc;ng kh&iacute; trong l&agrave;nh, cảm nhận những điều b&igrave;nh y&ecirc;n ở chốn n&uacute;i rừng xa x&ocirc;i.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/11/di-sapa-khong-can-co-nguoi-yeu-chi-can-co-ban-than-09112018-21.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>M&igrave;nh vẫn th&iacute;ch nhất kh&ocirc;ng kh&iacute; ở Sapa.</em></p>\r\n\r\n<p>* Tổng kết chuyến đi của bọn m&igrave;nh chỉ khoảng 900.000Đ/người, vừa tiết kiệm vừa c&oacute; thể đi chơi n&ecirc;n m&igrave;nh cũng kh&aacute; h&agrave;i l&ograve;ng. Chuyến du lịch Sapa lần n&agrave;y c&oacute; hơi tiếc v&igrave; kh&ocirc;ng c&oacute; nhiều kinh ph&iacute; n&ecirc;n bọn m&igrave;nh kh&ocirc;ng đi Fansipan n&ecirc;n hi vọng chuyến đi lần sau vậy. Nhưng m&igrave;nh vẫn khuy&ecirc;n thật l&ograve;ng, c&aacute;c bạn n&ecirc;n đi Fansipan v&igrave; ở đ&oacute; đẹp tuyệt vời, vừa được đi c&aacute;p treo vừa được ngắm cảnh m&acirc;y tr&ocirc;i bồng bềnh, cảm gi&aacute;c rất tuyệt vời.</p>', '2019-12-19 09:10:05', 1, 4, 'tuoi-tre-tron-ven-phai-den-sapa-it-nhat-mot-lan.1576746604.jpg', 'tuoi-tre-tron-ven-phai-den-sapa-it-nhat-mot-lan.321.html'),
(4, 'Sapa – “Ở đây sương khói mờ nhân ảnh”', '\"Ở đây sương khói mờ nhân ảnh  Ai biết tình ai có đậm đà...!!!\"  Có một Sapa như ẩn như hiện trong sương mờ tản mạn. Tại sao người ta lại yêu Sapa nhiều đến thế?', '<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-1.jpg\" style=\"height:1014px; width:720px\" /></p>\r\n\r\n<p><em>Đ&acirc;y l&agrave; lần đầu ti&ecirc;n Th&uacute;y&nbsp;đặt ch&acirc;n đến Sapa. C&ocirc; bạn kh&ocirc;ng khỏi cho&aacute;ng ngợp trước cảnh sắc dung dị nhưng kh&ocirc;ng k&eacute;m phần ngọt ng&agrave;o l&agrave;m say đắm l&ograve;ng người.</em></p>\r\n\r\n<p>Đối với Ngọc Th&uacute;y, Sapa tựa hồ một khung trời kh&aacute;c hẳn. Sinh ra v&agrave; lớn l&ecirc;n tại th&agrave;nh phố Hồ Ch&iacute; Minh, c&ocirc; bạn 9X&nbsp;cho biết m&igrave;nh chưa bao giờ tưởng tượng ngo&agrave;i S&agrave;i G&ograve;n c&ocirc; tr&oacute;t thương ra&nbsp;vẫn c&ograve;n một v&ugrave;ng trời đ&aacute;ng y&ecirc;u&nbsp;đến như thế. Mọi thứ nơi đ&acirc;y đều tuyệt vời v&agrave; dường như c&oacute;&nbsp;thể chạm đến đ&aacute;y cảm x&uacute;c.&nbsp;</p>\r\n\r\n<p><img alt=\"Hành trình đi du lịch Sapa\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-2.jpg\" style=\"height:690px; width:920px\" /></p>\r\n\r\n<p><em>Thật lạ kỳ khi Sapa kh&ocirc;ng hề ho&agrave;nh tr&aacute;ng với những t&ograve;a nh&agrave; cao ch&oacute;t v&oacute;t, chẳng c&oacute; khu vui chơi ri&ecirc;ng biệt, đường x&aacute; thuận tiện lưu th&ocirc;ng...Ấy thế m&agrave; ai nấy đều tr&oacute;t &quot;phải l&ograve;ng&quot; đến qu&ecirc;n ăn mất ngủ .Thế mới thấy những g&igrave; ch&acirc;n thật nhất, dung dị nhất lại l&agrave; thứ đ&aacute;ng được tr&acirc;n qu&yacute; biết nhường n&agrave;o.</em></p>\r\n\r\n<p>Trong h&agrave;nh tr&igrave;nh n&agrave;y, Th&uacute;y chọn địa điểm t&aacute; t&uacute;c tại bản C&aacute;t C&aacute;t c&aacute;ch thị trấn Sa Pa, L&agrave;o Cai khoảng 3 km, thuộc x&atilde; San Sả Hồ, huyện Sa Pa, tỉnh L&agrave;o Cai. Nơi đ&acirc;y l&agrave; một trong những điểm đến hấp dẫn nhất lu&ocirc;n được du kh&aacute;ch ưu &aacute;i lựa chọn mỗi khi l&ecirc;n phố n&uacute;i. C&ocirc; bạn quyết định qua đ&ecirc;m&nbsp;tại bản C&aacute;t C&aacute;t.Quả kh&ocirc;ng sai khi nghỉ đ&ecirc;m tại đ&acirc;y v&igrave; s&aacute;ng h&ocirc;m sau khi c&ocirc; n&agrave;ng bừng tỉnh giấc cũng l&agrave; l&uacute;c cảnh vật tựa chốn bồng lai ti&ecirc;n cảnh hiện ra lay động l&ograve;ng người.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-21.jpg\" style=\"height:920px; width:920px\" /></p>\r\n\r\n<p><em>Sớm mai se lạnh l&agrave; thời điểm tuyệt vời đề thưởng thức t&aacute;ch c&agrave; ph&ecirc; ấm&nbsp;n&oacute;ng.&nbsp;</em></p>\r\n\r\n<p>Với mục đ&iacute;ch đến Sapa chinh&nbsp; phục m&acirc;y ng&agrave;n, c&ocirc; bạn t&ocirc;i chắc chắn kh&ocirc;ng thể bỏ qua đỉnh Fansipan. V&igrave; thế, từ tờ mờ s&aacute;ng, c&ocirc; bạn đ&atilde; dậy thật sớm l&ecirc;n đường chinh phục ngọn n&uacute;i c&oacute; độ cao&nbsp;3.143 m&eacute;t.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-3.jpg\" style=\"height:613px; width:920px\" /></p>\r\n\r\n<p><em>Chỉ&nbsp;đơn giản l&agrave; nếp nh&agrave; tranh c&ugrave;ng kh&oacute;m hoa cải v&agrave;ng trước s&acirc;n th&ocirc;i m&agrave; sao lại đẹp đến nao l&ograve;ng.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-4.jpg\" style=\"height:690px; width:920px\" /></p>\r\n\r\n<p><em>Đến Sapa v&agrave;o độ&nbsp;th&aacute;ng 3 cũng l&agrave; l&uacute;c bạn được chi&ecirc;m ngưỡng hoa cải nhuộm v&agrave;ng cả một g&oacute;c trời - Ảnh: Internet.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-5.jpg\" style=\"height:613px; width:920px\" /></p>\r\n\r\n<p><em>Con đường dẫn l&ecirc;n đỉnh Fansipan, nơi ngắm nh&igrave;n th&agrave;nh phố treo lơ lửng giữa m&acirc;y ng&agrave;n. Người ta vẫn bảo nhau tại Sapa c&oacute; &quot;nấc thang dẫn đến thi&ecirc;n đ&agrave;ng&quot; v&agrave; ch&iacute;nh&nbsp;cung đường n&agrave;y đ&atilde; minh chứng cho điều đ&oacute;.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-7.jpg\" style=\"height:613px; width:920px\" /><em>Cuối&nbsp; c&ugrave;ng cũng đ&atilde; đến nơi rồi!</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-8.jpg\" style=\"height:613px; width:920px\" /></p>\r\n\r\n<p><em>Từ độ cao tr&ecirc;n 3.000 m&eacute;t, bạn sẽ được tận mắt chi&ecirc;m ngưỡng khung cảnh thi&ecirc;n nhi&ecirc;n, n&uacute;i rừng&nbsp;h&ugrave;ng vĩ.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-12.jpg\" style=\"height:613px; width:920px\" /></em></p>\r\n\r\n<p><em>Leo l&ecirc;n đến đỉnh n&uacute;i rồi bạn sẽ thấy c&ocirc;ng sức thức dậy thật sớm của m&igrave;nh chẳng hề hoang ph&iacute; một ch&uacute;t n&agrave;o. Ngo&agrave;i việc tự m&igrave;nh chinh phục từng &quot;nấc thang l&ecirc;n thi&ecirc;n đ&agrave;ng&quot;, bạn cũng c&oacute; thể sử dụng c&aacute;p treo với gi&aacute;&nbsp;v&eacute; d&agrave;nh cho người lớn l&agrave; 600.000 đồng, gi&aacute; v&eacute; trẻ em cao từ 1-1,3 m l&agrave; 400.000 đồng. Trẻ em cao dưới 1 m sẽ được miễn ph&iacute; gi&aacute; v&eacute;. V&eacute; đi c&aacute;p treo l&agrave; v&eacute; khứ hồi cho cả hai chiều. Mỗi cabin sức chứa từ 30 - 35 người.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-13.jpg\" style=\"height:613px; width:920px\" /></p>\r\n\r\n<p>C<em>hưa bao giờ m&acirc;y ở gần ta đến thế.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-15.jpg\" style=\"height:690px; width:920px\" /></p>\r\n\r\n<p><em>Chỉ cần khẽ kh&agrave;ng đưa tay ra đ&atilde; c&oacute; thể chạm v&agrave;o m&acirc;y rồi.</em><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-17.jpg\" style=\"height:690px; width:920px\" /></p>\r\n\r\n<p><em>Cổng trời lấp l&oacute; giữa tầng tầng lớp lớp m&acirc;y mờ.</em></p>\r\n\r\n<p>Tạm biệt Fansipan, c&ocirc; bạn Ngọc Th&uacute;y sẽ đưa ch&uacute;ng ta quay về bản C&aacute;t C&aacute;t v&agrave; trải nghiệm một ng&agrave;y h&oacute;a th&acirc;n th&agrave;nh n&agrave;ng sơn cước.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-19.jpg\" style=\"height:613px; width:920px\" /></p>\r\n\r\n<p><em>Đến Sapa m&agrave; kh&ocirc;ng thử thu&ecirc; trang phục của đồng b&agrave;o d&acirc;n tộc M&ocirc;ng, tận hưởng một ng&agrave;y h&oacute;a th&acirc;n th&agrave;nh sơn nữ th&igrave; quả l&agrave; đ&aacute;ng tiếc lắm đấy.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-20.jpg\" style=\"height:1380px; width:920px\" /></em></p>\r\n\r\n<p><em>Dạo một v&ograve;ng khắp c&aacute;c bản l&agrave;ng...</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-22.jpg\" style=\"height:613px; width:920px\" /></p>\r\n\r\n<p><em>Ngắm nh&igrave;n vẻ đẹp đất trời trong sương sớm.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-23.jpg\" style=\"height:613px; width:920px\" /></em></p>\r\n\r\n<p><em>Từng nh&agrave;nh c&acirc;y, ngọn cỏ nơi đ&acirc;y cũng đều đ&aacute;ng y&ecirc;u đến lạ.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-24.jpg\" style=\"height:613px; width:920px\" /></em></p>\r\n\r\n<p><em>H&atilde;y đến v&agrave; tr&ograve; chuyện c&ugrave;ng người d&acirc;n bản địa, bạn sẽ thu về nhiều kiến thức hay ho đấy.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-25.jpg\" style=\"height:613px; width:920px\" /></p>\r\n\r\n<p><em>Ngồi ăn đồ nướng về đ&ecirc;m l&agrave; điều kh&ocirc;ng thể thiếu khi đến với Sapa.&nbsp;</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Sapa-O-day-suong-khoi-mo-nhan-anh-23-10-2018-27.jpg\" style=\"height:613px; width:920px\" /></em></p>\r\n\r\n<p><em>Về đ&ecirc;m nhiệt độ Sapa xuống thấp v&igrave; vậy nếu được ngồi nh&acirc;m nhi những que thịt nướng n&oacute;ng hổi b&ecirc;n bếp than hồng th&igrave; c&ograve;n g&igrave; bằng.</em></p>\r\n\r\n<p>Đ&oacute; l&agrave; c&acirc;u chuyện về một &quot;sớm mai thức dậy lạc v&agrave;o hư kh&ocirc;ng&quot; của c&ocirc; bạn 9X Ngọc Th&uacute;y, c&ograve;n bạn th&igrave; sao? Bạn đ&atilde; sẵn s&agrave;ng để đến Sapa săn m&acirc;y ng&agrave;n chưa?</p>', '2019-12-19 09:09:58', 1, 4, 'sapa-o-day-suong-khoi-mo-nhan-anh.1576746598.jpg', 'sapa-o-day-suong-khoi-mo-nhan-anh.150.html');
INSERT INTO `tb_news` (`id`, `tieude`, `tomtat`, `noidung`, `ngaydang`, `id_user`, `id_diemden`, `avatar`, `slug`) VALUES
(5, 'Review chi tiết trải nghiệm Sapa – Hà Nội 4N3Đ chỉ với 6 triệu đồng', 'Khoảnh khắc giao mùa của đất trời giữa cuối thu và lập đông là thời điểm tuyệt vời hơn hết để cảm nhận một Hà Nội thật nồng nàn và một Sapa tuyệt đẹp. Mình bắt đầu chuyến đi vào một ngày Sài Gòn hanh nắng và ngột ngạt với bộn bề công việc.', '<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-1.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>Sapa đẹp lắm ai ơi!</em></p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; lịch tr&igrave;nh chi tiết cho chuyến đi trải nghiệm H&agrave; Nội 1 ng&agrave;y v&agrave; Sapa 3N2D của m&igrave;nh.</p>\r\n\r\n<h2><strong>CHUẨN BỊ TRƯỚC CHUYẾN ĐI</strong></h2>\r\n\r\n<p>- Trước một th&aacute;ng, bạn cần chuẩn bị thật kỹ:</p>\r\n\r\n<ul>\r\n	<li><strong>Thời gian:</strong>&nbsp;Ngo&agrave;i việc xếp lịch du lịch ph&ugrave; hợp với thời gian c&ocirc;ng việc, th&igrave; bạn n&ecirc;n v&agrave;o mục Forecast weather để xem dự b&aacute;o thời tiết của H&agrave; Nội v&agrave; Sapa trong những ng&agrave;y bạn dự định đi nh&eacute;. V&igrave; nếu chẳng may bạn đi tr&uacute;ng những ng&agrave;y mưa nhiều hoặc nhiều sương m&ugrave; sẽ ảnh hưởng rất nhiều đến chuyến đi của bạn. L&uacute;c m&igrave;nh đi thật may mắn m&igrave;nh chọn đ&uacute;ng ng&agrave;y nắng đẹp, m&acirc;y vừa n&ecirc;n thuận tiện cho việc sống ảo hơn.</li>\r\n	<li><strong>V&eacute; m&aacute;y bay:&nbsp;</strong>M&igrave;nh book v&eacute; m&aacute;y bay khứ hồi từ SG đến HN của Vietjet air với tổng chi ph&iacute; khứ hồi l&agrave;: 2.200.000VNĐ/ người. H&agrave;nh l&yacute; dưới 7kg sẽ được&nbsp;miễn ph&iacute; nh&eacute;!</li>\r\n</ul>\r\n\r\n<p>V&igrave; c&oacute; dự định chơi ở H&agrave; Nội&nbsp;một&nbsp;ng&agrave;y n&ecirc;n m&igrave;nh chọn giờ bay v&agrave;o ban đ&ecirc;m l&uacute;c 21h, tới HN tầm 23h.</p>\r\n\r\n<ul>\r\n	<li><strong>Book homestay ở H&agrave; Nội v&agrave; Sapa:</strong></li>\r\n</ul>\r\n\r\n<p>C&oacute; 2 phương &aacute;n để lựa chọn: Bạn c&oacute; thể bay đến H&agrave; Nội rồi xuất ph&aacute;t đến Sapa lu&ocirc;n nếu như kh&ocirc;ng c&oacute; &yacute; định v&ograve;ng quanh chơi ở H&agrave; Nội.&nbsp;</p>\r\n\r\n<p>C&ograve;n nếu như m&igrave;nh, th&igrave; ở H&agrave; Nội chơi đ&atilde; rồi khuya h&ocirc;m sau m&igrave;nh mới bắt đầu h&agrave;nh tr&igrave;nh đến Sapa. Kinh nghiệm của m&igrave;nh l&agrave; n&ecirc;n tham quan H&agrave; Nội&nbsp;trước, v&igrave; từ Sapa trở về bạn chẳng c&ograve;n t&acirc;m tr&iacute; đ&acirc;u để đi chơi ở H&agrave; Nội nữa. V&igrave; sapa đẹp đến mức h&uacute;t hồn bạn mất rồi!</p>\r\n\r\n<p>Ở H&agrave; Nội, m&igrave;nh chọn Hola Homestay. Nằm ở trung t&acirc;m th&agrave;nh phố đ&uacute;ng ngay khu phố cổ v&agrave; Hồ Gươm, Lăng B&aacute;c. Ưu điểm của homestay l&agrave; nằm ở trung t&acirc;m, gi&aacute; hợp l&yacute; v&agrave; tương đối sạch sẽ, c&oacute; đầy đủ tiện nghi như hotel như: nước n&oacute;ng lạnh, tivi, điều ho&agrave;,.... Gi&aacute;: 350.000VNĐ/đ&ecirc;m. Kh&ocirc;ng cần đặt cọc trước.</p>\r\n\r\n<p>Ở Sapa, m&igrave;nh chọn Littel View Homestay. Ấn tượng của homestay khi vừa bước v&agrave;o đ&oacute; l&agrave; một căn nh&agrave; nhỏ, được b&agrave;i tr&iacute; ấm c&uacute;ng v&agrave; dễ thương. Nhưng sau khi được anh chủ giới thiệu v&agrave; dắt l&ecirc;n ph&ograve;ng, th&igrave; th&ocirc;i rồi ngỡ ng&agrave;ng v&igrave; căn ph&ograve;ng qu&aacute; sạch sẽ, view n&uacute;i Ho&agrave;ng Li&ecirc;n Sơn tuyệt đẹp. Nằm ngay trung t&acirc;m sapa tr&ecirc;n đường sầm uất nhất mang t&ecirc;n Cầu M&acirc;y. Gi&aacute; 350.000VNĐ - 400.000VNĐ/ đ&ecirc;m. Tuy nhi&ecirc;n, m&igrave;nh chọn g&oacute;i ở homestay v&agrave; dịch vụ xe tại homestay đưa đ&oacute;n từ H&agrave; Nội đến Sapa khứ hồi chỉ với 899.000VNĐ/người. Bao gồm cả ở 3N2D v&agrave; xe đưa đ&oacute;n H&agrave; Nội - Sapa&nbsp;khứ hồi, l&uacute;c về c&ograve;n chở tới tận S&acirc;n bay Nội B&agrave;i, phục vụ tận nơi lu&ocirc;n. Qu&aacute; x&aacute; tiện lợi, kh&ocirc;ng cần phải loay hoay đặt xe ri&ecirc;ng.&nbsp;</p>\r\n\r\n<p>Thời gian check in: 13h, check out: 11h</p>\r\n\r\n<p>Thế l&agrave; mọi c&ocirc;ng đoạn về chỗ ở v&agrave; xe đ&atilde; xong. V&agrave; thế l&agrave; chỉ cần x&aacute;ch balo l&ecirc;n v&agrave; l&ecirc;n đường th&ocirc;i!</p>\r\n\r\n<h2><strong>LỊCH TR&Igrave;NH CHI TIẾT:</strong></h2>\r\n\r\n<h3><em><strong>Ng&agrave;y 1: H&agrave; Nội</strong></em></h3>\r\n\r\n<p>Tụi m&igrave;nh tới H&agrave; Nội v&agrave;o l&uacute;c 23h, về homestay nhận ph&ograve;ng v&agrave; ngủ một hơi tới s&aacute;ng. Chuẩn bị dưỡng sức cho lịch tr&igrave;nh kh&aacute;m ph&aacute; H&agrave; Nội trong v&ograve;ng 1 ng&agrave;y.</p>\r\n\r\n<p>- 9h30 h&ocirc;m sau: Tham quan Lăng B&aacute;c.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-18.jpg\" style=\"height:642px; width:960px\" /></p>\r\n\r\n<p><em>Lăng&nbsp;B&aacute;c chỉ mở cửa thứ 3,5,7 v&agrave; đến 10h30, n&ecirc;n c&aacute;c bạn nhớ ch&uacute; &yacute; xem lịch trước khi đi nh&eacute;.</em></p>\r\n\r\n<p>- 11h: Ăn đặc sản H&agrave; Nội ở khu phố cổ gần lăng B&aacute;c lu&ocirc;n. Địa chỉ: 74 H&agrave;ng Quạt, quận ho&agrave;n Kiếm. Qu&aacute;n n&agrave;y ngon xuất sắc c&oacute; tiếng lu&ocirc;n ấy.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-17.jpg\" style=\"height:642px; width:960px\" /></p>\r\n\r\n<p><em>Ăn xong tụi m&igrave;nh c&oacute; đi lang thang khu phố cổ để chụp h&igrave;nh rồi về homestay nghỉ ngơi.</em></p>\r\n\r\n<p>- Chiều 4h: Xuất ph&aacute;t kh&aacute;m ph&aacute; đường ray xe lửa phố cổ nổi tiếng v&agrave; tham quan quanh Hồ Gươm. Nơi n&agrave;y rất thơ mộng, đ&uacute;ng v&agrave;o chiều thu n&ecirc;n H&agrave; Nội trở n&ecirc;n nhẹ nh&agrave;ng, kh&ocirc;ng qu&aacute; x&ocirc; bồ, nhộn nhịp như S&agrave;i G&ograve;n.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-19.jpg\" style=\"height:1077px; width:720px\" /></p>\r\n\r\n<p><em>H&agrave; Nội đậm chất của một thủ đ&ocirc; cổ, n&eacute;t rất ri&ecirc;ng. M&igrave;nh tin l&agrave; bạn sẽ th&iacute;ch khi đến tham quan cụm di t&iacute;ch n&agrave;y đấy!</em></p>\r\n\r\n<p>- 18h30: Tụi m&igrave;nh tấp v&ocirc; ăn ốc lu&ocirc;c nổi tiếng qu&aacute;n C&ocirc; c&acirc;m ngay khu phố cổ. Ấn tượng đối với m&igrave;nh l&agrave; ốc luộc cực sạch, thơm v&agrave; dai. Nước chấm lạ, thơm. M&igrave;nh ăn m&ecirc; lu&ocirc;n ấy.</p>\r\n\r\n<p>- Ăn xong, đi dạo một ch&uacute;t v&agrave; trở về để thu dọn h&agrave;nh l&iacute; bắt đầu l&ecirc;n xe tiếp tục cho h&agrave;nh tr&igrave;nh m&igrave;nh chờ đợi, Sapa!</p>\r\n\r\n<h3><em><strong>Ng&agrave;y 2: Chinh phục&nbsp;Fansipan</strong></em></h3>\r\n\r\n<p>Xuất ph&aacute;t ở xe Interbusline H&agrave; Nội ( trước khi tới giờ xe chạy nh&agrave; xe sẽ gọi điện x&aacute;c nhận hoặc nhắn tin cho bạn) v&agrave;o l&uacute;c 23h, ngủ một hơi đến tầm 4h30 s&aacute;ng h&ocirc;m sau bạn đ&atilde; đến Sapa rồi. Nhưng v&igrave; thời tiết kh&aacute; lạnh v&agrave; đến kh&aacute; sớm. N&ecirc;n nh&agrave; xe sẽ để bạn ngủ đến 6h s&aacute;ng mới gọi c&aacute;c h&agrave;nh kh&aacute;ch th&ocirc;ng b&aacute;o đ&atilde; đến nơi v&agrave; bắt đầu cho kh&aacute;ch xuống xe.</p>\r\n\r\n<p>- 6h: tụi m&igrave;nh đi bộ về homestay Little view homestay, m&igrave;nh rất ấn tượng con đường để v&agrave;o homestay, v&igrave; bạn phải đi bộ v&agrave;i bậc dốc. Vừa lạnh, khung cảnh bậc dốc ch&igrave;m trong s&aacute;ng sớm của Sapa nh&igrave;n rất &quot;t&igrave;nh&quot;. Hơi mệt nhưng thử đi nh&eacute;! V&igrave; giờ check in quy định l&agrave; 13h n&ecirc;n tui m&igrave;nh chưa được nhận ph&ograve;ng, m&agrave; chỉ để đồ ở homestay, tắm rửa ở ph&ograve;ng chung, l&ecirc;n đồ để đi chơi v&agrave; chiều về mới check in.</p>\r\n\r\n<p>- Ng&agrave;y đầu ti&ecirc;n tại Sapa tụi m&igrave;nh kh&aacute;m ph&aacute; Fansipan. V&eacute; 700.000VNĐ/ người, bạn kh&ocirc;ng phải cất c&ocirc;ng đi mua v&eacute; v&igrave; l&uacute;c vừa tới homestay sẽ được anh chủ hỏi thăm ảnh mua d&ugrave;m v&agrave; thu&ecirc; xe 100.000VNĐ/ng&agrave;y d&ugrave;m cho bạn lu&ocirc;n. Bạn chỉ việc c&oacute; v&eacute; trong tay, c&oacute; xe v&agrave; đi th&ocirc;i.&nbsp;</p>\r\n\r\n<p>- Nếu bạn muốn đi t&agrave;u lửa Mường Hoa&nbsp;đến ga Fansipan để trải nghiệm th&igrave; c&oacute; thể đi bộ ra Sun Plaza hoặc chạy xe v&agrave;o Plaza gửi xe, v&agrave;o trong mua v&eacute;, tại đ&acirc;y b&aacute;n đủ loại v&eacute;: v&eacute; đi Fansipan 700.000VNĐ, v&eacute; t&agrave;u lửa Mường Hoa: 50.000VNĐ, v&eacute; t&agrave;u lửa từ Fansipan l&ecirc;n đỉnh của Fansipan l&agrave; 70.000VNĐ (bạn c&oacute; thể đi bộ 600 bậc).</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-4.jpg\" style=\"height:1080px; width:720px\" /></p>\r\n\r\n<p><em>Check in tại Sun Plaza, H&agrave; Nội.</em></p>\r\n\r\n<p>- Kinh nghiệm của m&igrave;nh l&agrave; n&ecirc;n đi thử cả Mường Hoa v&agrave; v&eacute; t&agrave;u l&ecirc;n đỉnh Fansipan thay v&igrave; đi 600 bậc thang. V&igrave; l&ecirc;n đ&oacute; kh&ocirc;ng kh&iacute; rất rất lạnh v&agrave; kh&aacute; lo&atilde;ng, n&oacute;i 600 bậc vậy th&ocirc;i chứ m&igrave;nh đi thấy mệt lắm v&agrave; kh&oacute; thở nữa.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-10.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>L&uacute;c l&ecirc;n m&igrave;nh c&oacute; thể đi bằng t&agrave;u lửa hoặc c&aacute;p treo&nbsp;để dưỡng sức, l&uacute;c xuống m&igrave;nh đi bộ vừa đỡ mệt vừa để chụp h&igrave;nh n&egrave;.</em></p>\r\n\r\n<p>Cảnh tr&ecirc;n Fansipan th&igrave; th&ocirc;i kh&ocirc;ng c&ograve;n g&igrave; b&agrave;n c&atilde;i v&igrave; qu&aacute; đẹp. Cứ như bồng lai ti&ecirc;n cảnh vậy. Đến đ&acirc;y l&uacute;c trời đầy m&acirc;y v&agrave; nắng đẹp th&igrave; g&oacute;c n&agrave;o bạn cũng c&oacute; được tấm h&igrave;nh xuất sắc.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-7.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-9.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-11.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-12.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-8.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Bất ngờ v&agrave; ngẫn ngơ v&igrave; chẳng tin c&oacute; một địa điểm ở Việt Nam lại đẹp đến như vậy.</em></p>\r\n\r\n<p>- Trưa:&nbsp;&nbsp;Ăn g&agrave; nướng ti&ecirc;u xanh nh&agrave; h&agrave;ng Hải L&acirc;m. Ngon xuất sắc lu&ocirc;n nha. Bạn search sẽ c&oacute; địa chỉ qu&aacute;n nh&eacute;.</p>\r\n\r\n<p>- Tối:&nbsp;Tụi m&igrave;nh đi bộ dạo phố Cầu M&acirc;y, ăn đồ nướng, dạo quảng trường v&agrave; chụp h&igrave;nh ph&iacute;a b&ecirc;n trong to&agrave; Sun Plaza. Từ homestay đi bộ chỉ mất 3 ph&uacute;t&nbsp;l&agrave; tới trung t&acirc;m v&agrave; c&aacute;c h&agrave;ng qu&aacute;n b&aacute;n đồ nướng.</p>\r\n\r\n<h3><strong>Ng&agrave;y 3: Bản C&aacute;t C&aacute;t - &Ocirc; Quy Hồ (Cổng Trời)</strong></h3>\r\n\r\n<p><strong>Bản C&aacute;t C&aacute;t:</strong>&nbsp;Từ trung t&acirc;m chỉ mất tầm 15 ph&uacute;t để đến Bản C&aacute;t C&aacute;t. C&aacute;c bạn nữ c&oacute; thể thu&ecirc; đồ ở đầu bản để v&ocirc; sống ảo nha. Gi&aacute; thu&ecirc; đồ l&agrave; 50.000VNĐ (Đồ thay sẽ c&oacute; người giữ tại tiệm cho thu&ecirc;). Gi&aacute; v&eacute; v&agrave;o tham quan bản 70.000VNĐ/người. N&ecirc;n thu&ecirc; xe m&aacute;y để đến suối chụp h&igrave;nh nh&eacute;. V&igrave; bản kh&aacute; l&agrave; rộng v&agrave; d&agrave;i bạn đi bộ xong l&agrave;&nbsp;kh&ocirc;ng c&ograve;n sức chụp choẹt đ&acirc;u. M&igrave;nh thu&ecirc; xe&nbsp;10,000VNĐ/ chuyến, xe&nbsp;&ocirc;m đứng quanh bản n&ecirc;n mệt l&uacute;c n&agrave;o bạn c&oacute; thể nhờ c&aacute;c ch&uacute; chở.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-2.jpg\" style=\"height:642px; width:960px\" /></p>\r\n\r\n<p><em>H&oacute;a th&acirc;n th&agrave;nh n&agrave;ng sơn nữ.</em></p>\r\n\r\n<p>Dưới ch&acirc;n Bản C&aacute;t C&aacute;t vừa v&ocirc; bạn sẽ thấy Qu&aacute;n c&agrave; ph&ecirc; view cực đẹp t&ecirc;n l&agrave; Heaven Camp Site. Qu&aacute;n n&agrave;y view n&uacute;i v&agrave; Bản C&aacute;t C&aacute;t&nbsp;n&ecirc;n nhiều g&oacute;c sống ảo. Tuy nhi&ecirc;n, v&igrave; để tiết kiệm thời gian, m&igrave;nh chọn đi C&aacute;t C&aacute;t&nbsp;xong l&agrave; tiến thẳng đến cổng trời lu&ocirc;n n&ecirc;n cũng chưa c&oacute; dịp gh&eacute; qu&aacute;n n&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-6.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><em>Bản C&aacute;t C&aacute;t c&oacute; nhiều ng&oacute;c ng&aacute;ch dễ thương lắm, đ&uacute;ng chất n&uacute;i rừng T&acirc;y Bắc lu&ocirc;n!</em></p>\r\n\r\n<p>-&nbsp;<strong>Đ&egrave;o &Ocirc; Quy Hồ - cổng trời</strong>: Bạn chỉ cầm search &quot;Cổng trời &Ocirc; Quy Hồ&quot;,&nbsp;google map sẽ chỉ tận nơi lu&ocirc;n. V&igrave; đ&egrave;o kh&aacute; nguy hiểm v&agrave; lạnh n&ecirc;n m&igrave;nh nghĩ nếu l&agrave; nữ với nhau th&igrave; bạn n&ecirc;n thu&ecirc; taxi đi.&nbsp;Trước cổng trời c&oacute; một c&ocirc;ng tr&igrave;nh đang x&acirc;y, sẽ c&oacute; c&aacute;c bậc thang leo l&ecirc;n đỉnh n&uacute;i, bạn dừng ở đ&acirc;y chụp h&igrave;nh trước rồi hẵn chạy th&ecirc;m 500m nữa để tới cổng trời sống ảo nh&eacute;.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-15.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-14.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><em>Cảnh chỗ n&agrave;y cũng xuất sắc kh&ocirc;ng k&eacute;m.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-13.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-16.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><em>Tại cổng trời th&igrave; rất nhiều h&agrave;ng qu&aacute;n v&agrave; g&oacute;c trời để chụp, bạn tha hồ sống ảo v&agrave; ngắm trọn khung cảnh đ&egrave;o &ocirc; quy hồ v&agrave; Sapa ch&igrave;m trong sương, mờ ảo tuyệt đẹp.</em></p>\r\n\r\n<p>- Tối: Tụi m&igrave;nh đi ăn ở nh&agrave; h&agrave;ng A Phủ. M&igrave;nh vốn kh&ocirc;ng ăn c&aacute; được nhưng lần n&agrave;y mạo mụi thử m&oacute;n đặc sản C&aacute; tầm nướng tại qu&aacute;n. M&igrave;nh d&ugrave;ng c&ugrave;ng cơm trắng v&agrave; rau Sapa. Kh&ocirc;ng thể ngờ c&aacute; tầm nướng v&agrave; ướp thơm ngon, vị rất lạ, đảm bảo bạn sẽ m&ecirc; lu&ocirc;n ấy. Gi&aacute;: 200.000VNĐ/ dĩa c&aacute; tầm. Một bữa ăn mất tầm 350.000VNĐ/ 2 người.</p>\r\n\r\n<p>- May mắn tối h&ocirc;m ấy ở Quảng trường&nbsp;c&oacute; tổ chức Lễ Hội Khai Mạc M&ugrave;a Đ&ocirc;ng&nbsp;n&ecirc;n v&ocirc; c&ugrave;ng đ&ocirc;ng vui, tiếng nhạc T&acirc;y Bắc vang l&ecirc;n, trẻ em d&acirc;n tộc vui nhảy tưng bừng. Lần đầu m&igrave;nh thấy cảnh nhộn nhịp trong hơi lạnh 10 độ m&agrave; vui tới vậy ấy.</p>\r\n\r\n<h3><em><strong>Ng&agrave;y 4: Qu&aacute;n c&agrave; ph&ecirc; VIETTREKKING</strong></em></h3>\r\n\r\n<p>Nằm cuối đường Ho&agrave;ng Li&ecirc;n c&aacute;ch homestay tầm 5 ph&uacute;t đi xe, qu&aacute;n c&agrave; ph&ecirc; Viettrekking l&agrave; địa điểm bạn kh&ocirc;ng n&ecirc;n bỏ qua nếu muốn c&oacute; nhiều ảnh sống ảo.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-3.jpg\" style=\"height:640px; width:960px\" /><br />\r\n<em>Qu&aacute;n kh&ocirc;ng l&agrave;m m&igrave;nh thất vọng v&igrave; kh&ocirc;ng gian rất đẹp, view n&uacute;i to&agrave;n bộ v&agrave; c&ograve;n nh&igrave;n thấy xe lửa Mường Hoa nữa.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-20.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-21.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-22.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-25.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-24.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-26.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-23.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p><em>G&oacute;c n&agrave;o ở qu&aacute;n cũng chụp h&igrave;nh rất đẹp n&ecirc;n nếu bạn đi nhớ tranh thủ đi sớm tầm 8-9h nh&eacute;, v&igrave; qu&aacute;n rất đ&ocirc;ng muốn chụp phải đợi đ&oacute;.</em></p>\r\n\r\n<p>Gi&aacute; nước uống, v&agrave; đồ ăn trung b&igrave;nh tầm 35.000VNĐ - 40.000VNĐ nh&eacute;.</p>\r\n\r\n<h2><strong>TỔNG HỢP C&Aacute;C QU&Aacute;N ĂN NGON TẠI SAPA</strong></h2>\r\n\r\n<p>- Phở Sạch</p>\r\n\r\n<p>- Nh&agrave; h&agrave;ng Hải L&acirc;m</p>\r\n\r\n<p>- Nh&agrave; h&agrave;ng A Phủ</p>\r\n\r\n<p>- Qu&aacute;n nướng Phố Cầu M&acirc;y</p>\r\n\r\n<p>- C&agrave; ph&ecirc; Viettreking</p>\r\n\r\n<p>- C&agrave; ph&ecirc; Viet Emotion</p>\r\n\r\n<p>- Cafe L&aacute; Dao</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/Review-chi-tiet-trai-nghiem-ha-noi-sapa-4N3D-chi-voi-6-trieu-dong-12-12-2018-5.jpg\" style=\"height:1080px; width:720px\" /></p>\r\n\r\n<p><em>Qu&aacute;n C&agrave; ph&ecirc; Viet Emotion.</em></p>\r\n\r\n<h2><strong>Tổng kết sau chuyến đi:</strong></h2>\r\n\r\n<p><strong>Chi ph&iacute;: 6 triệu/người</strong></p>\r\n\r\n<p>Nếu n&oacute;i Sapa đẹp thật sự kh&ocirc;ng đ&uacute;ng v&igrave; Sapa tuyệt vời tr&ecirc;n cả chữ đẹp cộng th&ecirc;m cả chữ &quot;t&igrave;nh&quot;. Đến Sapa chẳng cần đi đ&acirc;u xa, chỉ cần bạn ở homestay, nghe tiếng s&aacute;o m&egrave;o vang vọng, ngắm d&atilde;y n&uacute;i Ho&agrave;ng Li&ecirc;n Sơn mờ ảo trong sương, những c&ocirc; g&aacute;i d&acirc;n tộc đ&egrave;o g&ugrave;i tr&ecirc;n lưng bước tr&ecirc;n con đường nhỏ trải d&agrave;i. Nhi&ecirc;u đ&oacute; th&ocirc;i, cũng đủ l&agrave;m bạn xi&ecirc;u l&ograve;ng rồi.</p>\r\n\r\n<p>Thật đấy, h&atilde;y đến v&agrave; thử trải nghiệm H&agrave; Nội - Sapa. M&igrave;nh tin rằng bạn sẽ kh&ocirc;ng hối hận đ&acirc;u!</p>', '2019-12-19 09:09:45', 1, 4, 'review-chi-tiet-trai-nghiem-sapa-ha-noi-4n3d-chi-voi-6-trieu-dong.1576746585.jpg', 'review-chi-tiet-trai-nghiem-sapa-ha-noi-4n3d-chi-voi-6-trieu-dong.391.html'),
(6, 'Du lịch Sapa: Khám phá khách sạn 5 sao siêu hot chẳng khác trời Âu', 'Mình mới có chuyến đi Sapa 3 ngày 2 đêm về . Lần này mình sẽ review cho mọi người về 2 khách sạn Sapa mình ở, bao gồm cảm nhận của mình về chất lượng dịch vụ, ưu nhược điểm và chia sẻ những vị trí sống ảo cụ thể tại khách sạn nhé.', '<h2><strong>Kh&aacute;ch sạn&nbsp;De La Coupole Sapa &ndash; Mgallery by Sofitel</strong></h2>\r\n\r\n<p>Kh&aacute;ch sạn mới khai trương h&ocirc;m 12/12, th&igrave; 17/12 m&igrave;nh l&ecirc;n. Kh&aacute;ch sạn n&oacute;i chung l&agrave; si&ecirc;u chất lượng, ưng &iacute; từng t&iacute; một lu&ocirc;n, kh&ocirc;ng c&oacute; một điểm g&igrave; để ch&ecirc; tr&aacute;ch cả. Điều quan trọng nữa l&agrave; gi&aacute; th&agrave;nh đ&uacute;ng l&agrave; KH&Aacute; RẺ so với những g&igrave; nhận được. Gi&aacute; ph&ograve;ng hạng standard kh&ocirc;ng&nbsp;rơi v&agrave;o lễ tết khoảng 2.200.000VNĐ/ 2 người/đ&ecirc;m.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-1-e1546231383725.jpg\" style=\"height:878px; width:720px\" /></p>\r\n\r\n<p><em>Kh&aacute;ch sạn&nbsp;De La Coupole Sapa &ndash; Mgallery by Sofitel</em></p>\r\n\r\n<p>B&igrave;nh thường m&igrave;nh đi du lịch cũng chỉ ở kh&aacute;ch sạn 4 sao&nbsp;trở xuống, th&igrave; chỉ nghỉ v&agrave; ăn s&aacute;ng, nhưng đến đ&acirc;y th&igrave; ho&agrave;n to&agrave;n kh&aacute;c. Bạn c&oacute; ở mấy ng&agrave;y sống ảo cũng kh&ocirc;ng hết &yacute;, khỏi cần đi đ&acirc;u lu&ocirc;n, chuẩn c&acirc;u mọi người hay v&iacute; &ldquo; Lạc giữa trời &Acirc;u&ldquo; , &ldquo;nước Ph&aacute;p&nbsp;thu nhỏ giữa l&ograve;ng Sapa &ldquo; &hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-2-e1546231437637.jpg\" style=\"height:874px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-4.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-13.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Khu&ocirc;n vi&ecirc;n b&ecirc;n ngo&agrave;i&nbsp;kh&aacute;ch sạn l&ecirc;n h&igrave;nh đẹp lắm lu&ocirc;n, cũng phong c&aacute;ch&nbsp;trời &Acirc;u.</em></p>\r\n\r\n<p>Từng chi tiết nhỏ nhất cũng thật sự đẹp v&agrave; v&ocirc; c&ugrave;ng tinh tế. MGallery ch&iacute;nh l&agrave; một kiệt t&aacute;c nghệ thuật được tạo n&ecirc;n nhờ sự kết hợp giữa văn h&oacute;a địa phương, lịch sử Ph&aacute;p cổ điển c&ugrave;ng bầu kh&ocirc;ng kh&iacute; hiện đại, rực rỡ của th&agrave;nh phố Sapa. Kho&aacute;c l&ecirc;n m&igrave;nh vẻ đẹp ho&agrave;ng kim của phong c&aacute;ch Ph&aacute;p cổ điển nhưng lại kh&ocirc;ng hề gi&agrave; nua so với những kh&aacute;ch sạn đồng trang, vừa đằm thắm vừa trẻ trung.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-5.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-6.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-8.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>Kh&ocirc;ng gian ở đ&acirc;y trang tr&iacute; xa hoa, sang chảnh, n&oacute;i chung l&agrave; bất k&igrave; g&oacute;c n&agrave;o, cứ giơ m&aacute;y l&ecirc;n chụp l&agrave; đẹp, l&agrave; ảo.</em></p>\r\n\r\n<p><em><strong>C&oacute; 6 nơi ch&iacute;nh để sống ảo:&nbsp;</strong></em></p>\r\n\r\n<p>Tầng 2 kh&aacute;ch sạn l&agrave; sảnh kh&aacute;ch sạn, xung quanh rất rộng v&agrave; c&oacute; rất rất nhiều g&oacute;c chụp, decor độc đ&aacute;o, m&igrave;nh c&ograve;n chưa chụp hết, c&oacute; quầy b&aacute;nh ngọt Ph&aacute;p&nbsp; cầu thang xoắn ốc&hellip;</p>\r\n\r\n<p>Tầng 7 kh&aacute;ch sạn&nbsp;c&oacute; khu Spa, ph&ograve;ng tập Gym, bể bơi bốn m&ugrave;a như lời anh lễ t&acirc;n th&igrave; l&agrave; đẹp nhất Ch&acirc;u &Aacute;&nbsp;hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-33.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>Phải c&ocirc;ng nhận hồ bơi đ&uacute;ng l&agrave; đẹp thiệt sự.</em></p>\r\n\r\n<p>Tầng 10 l&agrave; khu buffet s&aacute;ng v&agrave; chiếc cầu sống ảo. Ri&ecirc;ng khu ăn s&aacute;ng cũng qu&aacute; mức sang chảnh lu&ocirc;n, rộng ơi l&agrave; rộng, m&agrave; kh&ocirc;ng g&oacute;c n&agrave;o xấu.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-24.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-28.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-25.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-29.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-30.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-26.jpg\" style=\"height:720px; width:960px\" /><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-27.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>Buffet s&aacute;ng ở đ&acirc;y đồ đa dạng, đầu bếp nấu kh&aacute; ok, vừa miệng.</em></p>\r\n\r\n<ul>\r\n	<li>Đối diện khu ăn s&aacute;ng, cũng ở tầng 10 lu&ocirc;n, l&agrave; khu Bar Absinthe đẹp lung linh lu&ocirc;n. Nh&igrave;n đ&uacute;ng kiểu &quot;chanh sả&quot;, quyền lực. C&ograve;n chiếc cầu sống ảo tr&ecirc;n tầng&nbsp;10 chắc đi v&agrave;o huyền thoại lu&ocirc;n, chụp ở đấy nếu trời s&aacute;ng, với cả ai chụp m&aacute;y cơ th&igrave; sẽ thấy được hết c&aacute;i sự ho&agrave;nh tr&aacute;ng, lộng lẫy, y hệt đang ở trời &Acirc;u lu&ocirc;n. Tui chụp điện thoại, xong l&uacute;c mờ sương n&ecirc;n ảnh kh&ocirc;ng đẹp mấy như người ta.</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-32.jpg\" style=\"height:960px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-34.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-35.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Kh&ocirc;ng gian sang trọng, đẳng cấp.</em></p>\r\n\r\n<p>Trước cửa kh&aacute;ch sạn ch&iacute;nh l&agrave; Sun Plaza. Kh&aacute;ch sạn tọa lạc chuẩn trung t&acirc;m lu&ocirc;n, thuận tiện đi lại.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-01-01-2019-2.jpg\" style=\"height:969px; width:720px\" /></p>\r\n\r\n<p><em>Nơi sống ảo thần th&aacute;nh, nh&agrave; nh&agrave; check in, người người check in đ&oacute; ạ</em></p>\r\n\r\n<p>Ở tầng 2 mở cửa ra ngo&agrave;i vườn chụp, xong c&oacute; thể đi ra tầng 3 ra khu shopping của Sunworld, v&agrave; đi ra t&agrave;u ngắm Mường Hoa l&ecirc;n fansipan.Ngo&agrave;i ra, ở c&aacute;c tầng, h&agrave;nh lang, ban c&ocirc;ng, thang m&aacute;y &hellip; đều c&oacute; ảnh đẹp cả.</p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; một số h&igrave;nh ảnh về căn ph&ograve;ng m&agrave; m&igrave;nh đ&atilde; ở:</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-10-e1546231963718.jpg\" style=\"height:960px; width:719px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-12-e1546232047146.jpg\" style=\"height:905px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-11.jpg\" style=\"height:737px; width:960px\" /></p>\r\n\r\n<p><em>Mọi ng&oacute;c ng&aacute;ch đều c&oacute; thể dễ d&agrave;ng cho ra đời những bức ảnh sang chảnh.</em></p>\r\n\r\n<p>Nh&acirc;n vi&ecirc;n phục vụ rất tốt, đi đi lại lại trong kh&aacute;ch sạn, gặp bất k&igrave; ai cũng c&uacute;i đầu n&oacute;i c&acirc;u &ldquo; Xin ch&agrave;o&quot;&nbsp;&nbsp;kh&aacute; th&acirc;n thiện. L&uacute;c m&igrave;nh mới check in, c&oacute; anh nh&acirc;n vi&ecirc;n t&ecirc;n l&agrave; Bi&ecirc;n, hướng dẫn từng tầng tham quan c&aacute;c khu lu&ocirc;n. Khi m&igrave;nh về rồi, cũng c&oacute; viết mail cảm ơn, kh&aacute; l&agrave; chu đ&aacute;o.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-20.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-22.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-16.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<h2><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-15.jpg\" style=\"height:960px; width:720px\" /></h2>\r\n\r\n<p><em>Ph&ograve;ng ngủ sang trọng, &ecirc;m &aacute;i.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-17-e1546237101176.jpg\" style=\"height:900px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-21.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Nh&agrave; vệ sinh kh&aacute;ch sạn 5 sao.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-18.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Kh&aacute;ch sạn c&oacute; chuẩn bị sẵn &aacute;o cho&agrave;ng tắm.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-31-12-2018-19.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Kh&ocirc;ng phải n&oacute;i qu&aacute; chứ m&igrave;nh ưng từ đ&ocirc;i d&eacute;p trở đi.. Đ&ocirc;i d&eacute;p được thiết kế ri&ecirc;ng bởi ch&iacute;nh nh&agrave; thiết kế Bil Bensley lu&ocirc;n.&nbsp;&nbsp;</em></p>\r\n\r\n<h2><strong>Kh&aacute;ch sạn Pistachio&nbsp;</strong></h2>\r\n\r\n<p>Kh&aacute;ch sạn n&agrave;y l&agrave; kh&aacute;ch sạn&nbsp;4 sao&nbsp; cũng vừa khai trương 15/12. Gi&aacute; ph&ograve;ng đang được&nbsp;&nbsp;giảm nh&acirc;n dịp khai trương đến th&aacute;ng 3 nh&eacute; mọi người.&nbsp;Gi&aacute; ph&ograve;ng trong thời gian khai trương, kh&ocirc;ng rơi v&agrave;o lễ tết khoảng 1.250.000đ/2ng&agrave;y/đ&ecirc;m.&nbsp;</p>\r\n\r\n<p>Kh&aacute;ch sạn n&agrave;y th&igrave; decor cũng ok, hạng standard cũng c&oacute; ban c&ocirc;ng, kh&aacute; l&agrave; tho&aacute;ng.Vị tr&iacute; ngay trung t&acirc;m, di chuyển thuận tiện. Đi bộ một x&iacute;u l&agrave; đến sunplaza lu&ocirc;n n&egrave;.Điểm cộng lớn nhất l&agrave; nh&acirc;n vi&ecirc;n rất dễ thương, nhiệt t&igrave;nh.&nbsp;</p>\r\n\r\n<p>Kh&aacute;ch sạn cũng đẹp, c&oacute; bể bơi, spa, bar&hellip; nhưng v&igrave; m&igrave;nh chụp b&ecirc;n kh&aacute;ch sạn kia cảm thấy đủ rồi, n&ecirc;n sang đ&acirc;y kh&ocirc;ng c&ograve;n hứng chụp mấy nữa.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-01-01-2019-20.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>Ban c&ocirc;ng kh&aacute;ch sạn tạo điều kiện cho bạn ngắm m&acirc;y trời.</em></p>\r\n\r\n<p>Điều m&igrave;nh kh&ocirc;ng h&agrave;i l&ograve;ng ở kh&aacute;ch sạn n&agrave;y l&agrave; đ&ocirc;i d&eacute;p. Đ&ocirc;i d&eacute;p đi trong ph&ograve;ng thật sự ch&aacute;n lu&ocirc;n, mỏng t&egrave;o hơn d&eacute;p vietnam airline, đi kh&ocirc;ng cẩn thận ng&atilde; sấp mặt. Buffet s&aacute;ng cũng b&igrave;nh thường, chưa được phong ph&uacute; cho lắm. Hoặc l&agrave; do b&ecirc;n Hotel de la coupole n&oacute; hịn qu&aacute; n&ecirc;n m&igrave;nh cứ bị so s&aacute;nh &yacute;.</p>\r\n\r\n<p>C&oacute; một điểm đặc biệt m&igrave;nh ph&aacute;t hiện ra s&aacute;t giờ về, wc tầng 6, cũng l&agrave; tầng sảnh lễ t&acirc;n &yacute;, view wc nh&igrave;n ra sunplaza đẹp d&atilde; man.</p>\r\n\r\n<p>C&aacute;c bạn ạ, m&igrave;nh kh&ocirc;ng hiểu mấy về c&aacute;c tiểu chuẩn của kh&aacute;ch sạn, n&ecirc;n kh&ocirc;ng biết m&igrave;nh c&oacute; hỏi ngu kh&ocirc;ng. Hai kh&aacute;ch sạn tr&ecirc;n trong nh&agrave; tắm đều kh&ocirc;ng c&oacute; d&eacute;p đi ri&ecirc;ng. M&agrave; m&igrave;nh ở nh&agrave; quen đi d&eacute;p &yacute;, kh&ocirc;ng c&oacute; d&eacute;p đi v&agrave;o nh&agrave; tắm, trời n&agrave;y lạnh lắm c&aacute;c bạn ạ.</p>\r\n\r\n<h2><strong>Một số kinh nghiệm trong chuyến đi n&agrave;y</strong></h2>\r\n\r\n<p>-&nbsp; M&igrave;nh ph&aacute;t hiện ra b&igrave;nh thường mọi người cứ chụp ảnh trước nh&agrave; thờ, nhưng sau nh&agrave; thờ l&ecirc;n ảnh đẹp lắm&nbsp;mọi người ạ.</p>\r\n\r\n<p>- M&igrave;nh c&oacute; tham khảo rất nhiều review tốt cho Nh&agrave; h&agrave;ng Hải L&acirc;m, v&agrave; c&oacute; n&oacute;i m&oacute;n g&agrave; nướng ti&ecirc;u xanh l&agrave; best seller. Tuy nhi&ecirc;n, với khẩu vị của m&igrave;nh, thấy g&agrave; ngon, nhưng sốt kh&ocirc;ng ngon lắm, cũng kh&ocirc;ng thơm như mọi người tả. C&oacute; thể do số m&igrave;nh đen chăng. M&oacute;n b&ograve; nướng ống tre vừa miệng hơn, nhưng sốt 2 m&oacute;n c&oacute; phần giống nhau. Canh chua c&aacute; tầm cũng bị chua qu&aacute; mức quy định, trong khi m&igrave;nh l&agrave; người cực k&igrave; th&iacute;ch chua m&agrave; c&ograve;n thấy thế. Ăn ở đ&oacute; đọc hội sapa tất tần tật l&agrave; được giảm 10% nha mọi người.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-01-01-2019-3.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-01-01-2019-4-e1546393400915.jpg\" style=\"height:823px; width:720px\" /></p>\r\n\r\n<p><em>Những m&oacute;n ăn &quot;đặc sản&quot; của Sapa.</em></p>\r\n\r\n<p>-&nbsp; M&igrave;nh mua hạt dẻ&nbsp;ở chỗ cạnh nh&agrave; h&agrave;ng &Ocirc; Quy Hồ, do anh taxi giới thiệu. Ra đấy người ta bảo một loại l&agrave; hạt dẻ nếp 100.000đ/kg, một loại hạt dẻ tẻ 70.000đ/kg. Nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng bảo hạt dẻ nếp b&oacute;ng đẹp, hạt đều mẩy hơn. Thế nhưng b&ecirc;n hạt nếp th&igrave; n&oacute;ng vừa quay xong, b&ecirc;n tẻ th&igrave; nguội, m&igrave;nh đ&atilde; nghi nghi rồi, nhưng vẫn mua loại 100.000đ. Về nh&agrave; search tr&ecirc;n mạng thấy l&agrave;m g&igrave;&nbsp;c&oacute; hạt dẻ tẻ đ&acirc;u nhỉ. Bị lừa c&aacute;c b&aacute;c ạ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-01-01-2019-5-e1546393490557.jpg\" style=\"height:780px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-01-01-2019-6-e1546393515243.jpg\" style=\"height:792px; width:720px\" /></p>\r\n\r\n<p><em>Hạt dẻ&nbsp;ở chỗ cạnh nh&agrave; h&agrave;ng &Ocirc; Quy Hồ.</em></p>\r\n\r\n<p><strong><em>Những điểm đẹp định đi:&nbsp;</em></strong></p>\r\n\r\n<ul>\r\n	<li>Đ&egrave;o &Ocirc; Quy Hồ</li>\r\n	<li>Viet Trekking Home Sapa ( m&igrave;nh thấy qu&aacute;n n&egrave; c&ograve;n ưng hơn Haven Sapa Campsite). Qu&aacute;n n&agrave;y chụp view ra t&agrave;u đi Mường Hoa, v&agrave; b&ecirc;n cạnh qu&aacute;n c&oacute; một mỏm đ&aacute; sống ảo, view ra n&uacute;i bao la b&aacute;t ng&aacute;t lu&ocirc;n.</li>\r\n	<li>Trong bản C&aacute;t C&aacute;t c&oacute; điểm mới &ldquo; C&acirc;y th&ocirc;ng hết c&ocirc; đơn &ldquo;, với tr&aacute;i tim khổng lồ lồng giữa 2 c&acirc;y th&ocirc;ng, khỏi cần v&ocirc; Đ&agrave; Lạt lu&ocirc;n .</li>\r\n	<li>&nbsp;Sapa Jade Hill nếu kh&ocirc;ng nghỉ lại, c&oacute; thể mua v&eacute; v&agrave;o chụp ảnh, 150k/người, nếu th&ecirc;m set tr&agrave; b&aacute;nh&nbsp; 300k/người.</li>\r\n	<li>Silk Path v&agrave;o uống cafe khoảng 200.000đ/người.</li>\r\n	<li>&nbsp;L&aacute; Dao Spa &amp; Coffee House ở bản Tả Van cũng đẹp nh&eacute;. Ở đ&oacute; vừa tắm l&aacute; thuốc, vừa cafe view đẹp, vừa ăn g&agrave; nướng lu&ocirc;n.</li>\r\n	<li>&nbsp;Nhưng cuối c&ugrave;ng l&ecirc;n Fansipan lần n&agrave;y được nắng đẹp, với cả mệt, cảm thấy đủ ảnh rồi, n&ecirc;n những điểm &ldquo;định đi &ldquo; kia m&igrave;nh bỏ qua hết lu&ocirc;n M&igrave;nh viết ra gợi &yacute; cho ai sắp đi th&igrave; c&oacute; thể đi nh&eacute; ^^</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-01-01-2019-8.jpg\" style=\"height:960px; width:836px\" /></p>\r\n\r\n<p><em>Ngồi c&aacute;p treo l&ecirc;n đỉnh Fansipan.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-01-01-2019-7.jpg\" style=\"height:540px; width:960px\" /></p>\r\n\r\n<p><em>Check in ở độ cao 3.143 m&eacute;t.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/12/khach-san-lac-buoc-troi-au-01-01-2019-9.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>Nh&acirc;m nhi c&agrave; ph&ecirc; v&agrave; ngắm m&acirc;y trời.</em></p>\r\n\r\n<p>B&ecirc;n tr&ecirc;n&nbsp;l&agrave; &quot;tất tần tật&quot; những&nbsp;trải nghiệm sau chuyến đi. Hy vọng c&oacute; &iacute;ch với ai quan t&acirc;m nha !!!</p>', '2019-12-19 09:09:33', 1, 4, 'du-lich-sapa-kham-pha-khach-san-5-sao-sieu-hot-chang-khac-troi-au.1576746572.jpg', 'du-lich-sapa-kham-pha-khach-san-5-sao-sieu-hot-chang-khac-troi-au.201.html'),
(7, 'Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một \"bản tình ca trắng\"', 'Hoa ban trắng Đà Lạt thường nở rộ vào đầu xuân và kéo dài đến đầu mùa hạ, nhưng hoa ban đầu mùa vẫn là đẹp nhất. Những cánh hoa ban trắng muốt nở bung ven đường làm Đà Lạt tạo nên một bức tranh đẹp vô cùng, làm say lòng biết bao lữ khách.', '<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một &amp;#34;bản tình ca trắng&amp;#34; - 1\" src=\"https://cdn.24h.com.vn/upload/1-2019/images/2019-01-06/Thang-1-hoa-ban-da-no-trang-troi-da-Lat-dep-tua-nhu-mot-1-1546775284-904-width650height406.jpg\" /></p>\r\n\r\n<p>Đ&agrave; Lạt vừa c&oacute; mai anh đ&agrave;o tựa Nhật Bản vừa c&oacute; hoa ban trắng đẹp như phim H&agrave;n. Bảo sao, người ta bảo Đ&agrave; Lạt đang độ đẹp nhất, &quot;t&igrave;nh&quot; nhất.</p>\r\n\r\n<p>Hoa ban đ&acirc;u chỉ c&oacute; ở T&acirc;y Bắc m&agrave; ngay ở Đ&agrave; Lạt cũng c&oacute; một m&ugrave;a hoa ban đẹp nao l&ograve;ng. Hoa ban T&acirc;y Bắc nằm tr&ecirc;n c&aacute;c triền n&uacute;i, ẩn khuất trong những ng&ocirc;i nh&agrave; nhỏ v&agrave; những con dốc lượn v&ograve;ng. C&ograve;n hoa ban Đ&agrave; Lạt lại được trồng tr&ecirc;n nhiều con đường. Truyền thuyết Hoa Ban gắn liền với chuyện t&igrave;nh y&ecirc;u m&atilde;nh liệt nhưng cũng đầy nước mắt giữa n&agrave;ng Ban v&agrave; ch&agrave;ng Khum, được v&iacute; như mối t&igrave;nh giữa Romeo v&agrave; Juliet của phương T&acirc;y.</p>\r\n\r\n<p>Nếu chỉ lướt qua, bạn sẽ kh&ocirc;ng cảm nhận hết vẻ đẹp của hoa ban trắng. H&atilde;y đi thật khẽ v&agrave; chậm r&atilde;i, khi đ&oacute; bạn c&oacute; thời gian nh&igrave;n ngắm, tĩnh l&ograve;ng mới thấy r&otilde;. C&aacute;i đẹp d&agrave;nh ri&ecirc;ng cho những t&acirc;m hồn nhạy cảm v&agrave; tinh tế như ch&iacute;nh linh hồn của mảnh đất Đ&agrave; Lạt.</p>\r\n\r\n<p>Hoa ban trắng nhuốm c&aacute;i lạnh của cả một m&ugrave;a đ&ocirc;ng Đ&agrave; Lạt, rụng hết l&aacute; v&agrave; giờ đ&acirc;y ra hoa. Đứng tr&ecirc;n cao nh&igrave;n xuống, cả con đường ngập tr&agrave;n hoa trắng thấy thanh khiết, rộn r&agrave;ng l&agrave;m sao. Những c&acirc;y hoa ban tạo d&aacute;ng kh&aacute;c nhau. C&oacute; c&acirc;y thẳng h&agrave;ng, c&oacute; c&acirc;y đơn độc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thời gian n&agrave;y, cả Đ&agrave; Lạt lại ngập tr&agrave;n trong sắc trắng của hoa Ban. Dạo quanh từng con phố, nh&igrave;n những c&agrave;nh hoa nở rộ giữa trời mới cảm nhận được hết vẻ đẹp của lo&agrave;i hoa n&agrave;y. Nếu bạn chẳng may vương vấn với vẻ đẹp của m&ugrave;a ban trắng Đ&agrave; Lạt hay tr&oacute;t say l&ograve;ng với những bức ảnh về lo&agrave;i hoa n&agrave;y tr&ecirc;n những con đường thơ mộng nơi phố n&uacute;i th&acirc;n thương, c&oacute; thể tự thưởng cho m&igrave;nh một chuyến đi v&agrave;o th&aacute;ng 1.</p>\r\n\r\n<p>Bạn c&oacute; thể ngắm hoa ở những con đường như: Trần Ph&uacute; - nh&agrave; thờ Con G&agrave; - Dinh 2, Quang Trung - Ga Đ&agrave; Lạt - Ng&atilde; 4 Phan Chu Trinh, Ph&ugrave; Đổng Thi&ecirc;n Vương... Đặc biệt l&agrave; đường Trần Ph&uacute;, chỉ một đoạn d&agrave;i khoảng 1,5 km nhưng c&oacute; h&agrave;ng trăm c&acirc;y hoa ban đang thi nhau nở th&agrave;nh ch&ugrave;m trắng x&oacute;a, nhuộm m&agrave;u cả một v&ugrave;ng.</p>\r\n\r\n<p>Anh ơi! Đi Đ&agrave; Lạt với em, v&igrave; hoa ban đang nở trắng ngập trời rồi!</p>\r\n\r\n<p><img alt=\"Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một &amp;#34;bản tình ca trắng&amp;#34; - 2\" src=\"https://cdn.24h.com.vn/upload/1-2019/images/2019-01-06/Thang-1-hoa-ban-da-no-trang-troi-da-Lat-dep-tua-nhu-mot-2-1546775284-629-width650height433.jpg\" /></p>\r\n\r\n<p><img alt=\"Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một &amp;#34;bản tình ca trắng&amp;#34; - 3\" src=\"https://cdn.24h.com.vn/upload/1-2019/images/2019-01-06/Thang-1-hoa-ban-da-no-trang-troi-da-Lat-dep-tua-nhu-mot-3-1546775284-493-width650height650.jpg\" /></p>\r\n\r\n<p><img alt=\"Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một &amp;#34;bản tình ca trắng&amp;#34; - 4\" src=\"https://cdn.24h.com.vn/upload/1-2019/images/2019-01-06/Thang-1-hoa-ban-da-no-trang-troi-da-Lat-dep-tua-nhu-mot-4-1546775284-576-width650height433.jpg\" /></p>\r\n\r\n<p><img alt=\"Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một &amp;#34;bản tình ca trắng&amp;#34; - 5\" src=\"https://cdn.24h.com.vn/upload/1-2019/images/2019-01-06/Thang-1-hoa-ban-da-no-trang-troi-da-Lat-dep-tua-nhu-mot-5-1546775284-570-width650height813.jpg\" /></p>\r\n\r\n<p><img alt=\"Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một &amp;#34;bản tình ca trắng&amp;#34; - 6\" src=\"https://cdn.24h.com.vn/upload/1-2019/images/2019-01-06/Thang-1-hoa-ban-da-no-trang-troi-da-Lat-dep-tua-nhu-mot-6-1546775284-76-width650height406.jpg\" /></p>\r\n\r\n<p><img alt=\"Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một &amp;#34;bản tình ca trắng&amp;#34; - 7\" src=\"https://cdn.24h.com.vn/upload/1-2019/images/2019-01-06/Thang-1-hoa-ban-da-no-trang-troi-da-Lat-dep-tua-nhu-mot-7-1546775284-674-width650height813.jpg\" /></p>\r\n\r\n<p><img alt=\"Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một &amp;#34;bản tình ca trắng&amp;#34; - 8\" src=\"https://cdn.24h.com.vn/upload/1-2019/images/2019-01-06/Thang-1-hoa-ban-da-no-trang-troi-da-Lat-dep-tua-nhu-mot-8-1546775284-380-width650height433.jpg\" /></p>\r\n\r\n<p><img alt=\"Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một &amp;#34;bản tình ca trắng&amp;#34; - 9\" src=\"https://cdn.24h.com.vn/upload/1-2019/images/2019-01-06/Thang-1-hoa-ban-da-no-trang-troi-da-Lat-dep-tua-nhu-mot-9-1546775284-902-width650height755.jpg\" /></p>\r\n\r\n<p><img alt=\"Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một &amp;#34;bản tình ca trắng&amp;#34; - 10\" src=\"https://cdn.24h.com.vn/upload/1-2019/images/2019-01-06/Thang-1-hoa-ban-da-no-trang-troi-da-Lat-dep-tua-nhu-mot-10-1546775284-824-width650height650.jpg\" /></p>\r\n\r\n<p><img alt=\"Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một &amp;#34;bản tình ca trắng&amp;#34; - 11\" src=\"https://cdn.24h.com.vn/upload/1-2019/images/2019-01-06/Thang-1-hoa-ban-da-no-trang-troi-da-Lat-dep-tua-nhu-mot-11-1546775284-122-width650height406.jpg\" /></p>\r\n\r\n<p><img alt=\"Tháng 1 hoa ban đã nở trắng trời, Đà Lạt đẹp tựa như một &amp;#34;bản tình ca trắng&amp;#34; - 12\" src=\"https://cdn.24h.com.vn/upload/1-2019/images/2019-01-06/Thang-1-hoa-ban-da-no-trang-troi-da-Lat-dep-tua-nhu-mot-12-1546775284-880-width650height488.jpg\" /></p>', '2019-12-19 09:09:20', 1, 7, 'thang-1-hoa-ban-da-no-trang-troi-da-lat-dep-tua-nhu-mot-ban-tinh-ca-trang.1576746560.jpeg', 'thang-1-hoa-ban-da-no-trang-troi-da-lat-dep-tua-nhu-mot-ban-tinh-ca-trang.190.html');
INSERT INTO `tb_news` (`id`, `tieude`, `tomtat`, `noidung`, `ngaydang`, `id_user`, `id_diemden`, `avatar`, `slug`) VALUES
(8, 'Mùa hồng đến rồi, có ai thoáng thấy nhớ Đà Lạt chưa?', 'Đà Lạt không chỉ gây thương nhớ bởi cái danh xưng \"xứ sở ngàn hoa\" mà còn khiến người ta trông ta ngóng bởi những cái hẹn chưa một lần giao kết.', '<p><img alt=\"Du lịch đà lạt\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Mua-hong-den-roi-co-ai-thoang-thay-nho-da-lat-chua-26-10-2018-1.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>M&ugrave;a hồng đến rồi, c&oacute; ai tho&aacute;ng thấy nhớ Đ&agrave; Lạt chưa?</em></p>\r\n\r\n<p>Cứ độ cuối th&aacute;ng 10 đầu th&aacute;ng 11, khi những c&agrave;nh hồng bắt đầu trơ trụi l&aacute;, chỉ c&ograve;n s&oacute;t lại những quả mộng&nbsp;thắm đỏ tr&ecirc;n c&agrave;nh, đấy cũng l&agrave; l&uacute;c m&ugrave;a hồng thật sự đến rồi. M&ugrave;a n&agrave;y, nếu c&oacute; dịp l&aacute;i con xe m&aacute;y, vi vu khắp c&aacute;c cung đường Đ&agrave; Lạt, bạn sẽ bất gi&aacute;c bắt gặp hai b&ecirc;n đường&nbsp;những quả hồng đang thắp lửa tr&ecirc;n cao.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Mua-hong-den-roi-co-ai-thoang-thay-nho-da-lat-chua-26-10-2018-2.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Mua-hong-den-roi-co-ai-thoang-thay-nho-da-lat-chua-26-10-2018-3.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Những quả hồng gi&ograve;n căng tr&ograve;n, mơn mởn g&oacute;p phần t&ocirc; điểm cho đất trời Đ&agrave; Lạt.</em></p>\r\n\r\n<p>M&ugrave;a Thu đ&atilde; qu&aacute; đủ để một Đ&agrave; Lạt vốn dĩ mang trong m&igrave;nh sự l&atilde;ng mạn, nền n&atilde;&nbsp;&quot;đốn tim&quot; người lữ kh&aacute;ch.&nbsp;Ấy m&agrave; giờ đ&acirc;y c&oacute; th&ecirc;m một vườn hồng n&ecirc;n thơ như thế n&agrave;y hỏi sao bao người kh&ocirc;ng &quot;cuồng ch&acirc;n&quot; cho đặng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Mua-hong-den-roi-co-ai-thoang-thay-nho-da-lat-chua-26-10-2018-5.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Mua-hong-den-roi-co-ai-thoang-thay-nho-da-lat-chua-26-10-2018-6.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Mua-hong-den-roi-co-ai-thoang-thay-nho-da-lat-chua-26-10-2018-9.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Niềm vui thấy thế m&agrave; đ&ocirc;i khi đơn giản lắm. Một chiều thu gh&eacute; Đ&agrave; Lạt ngắm vườn hồng, chỉ vậy th&ocirc;i m&agrave; bỗng thấy l&ograve;ng đơm&nbsp;hoa.</em></p>\r\n\r\n<p>Những ng&agrave;y n&agrave;y gh&eacute;&nbsp;Đ&agrave; Lạt kh&ocirc;ng kh&oacute; để t&igrave;m mua hồng. Những rổ hồng được b&agrave;y b&aacute;n khắp mọi nơi tr&ecirc;n phố, trong chợ Đ&agrave; Lạt. Thế nhưng, sẽ chẳng c&oacute; quả hồng lăn l&oacute;c n&agrave;o ngo&agrave;i kia giữ trọn vẹn c&aacute;i vị tinh t&uacute;y của đất trời như quả hồng ch&iacute;n vừa h&aacute;i tr&ecirc;n c&acirc;y. Nhấm nh&aacute;p một miếng hồng gi&ograve;n ng&agrave;y cuối thu dường như mọi mệt mỏi, căng thẳng thường nhật đều c&oacute; thể tan biến. Chỉ c&ograve;n lại ta - vườn hồng v&agrave; sự an y&ecirc;n.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Mua-hong-den-roi-co-ai-thoang-thay-nho-da-lat-chua-26-10-2018-12.jpg\" style=\"height:960px; width:720px\" /><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Mua-hong-den-roi-co-ai-thoang-thay-nho-da-lat-chua-26-10-2018-13.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Một thế giới tịnh tại thu nhỏ trong vườn hồng.</em></p>\r\n\r\n<p>Bạn thấy đấy, m&ugrave;a hồng đ&atilde; đến thật rồi.&nbsp;Nếu như bạn cũng giống c&ocirc; bạn Nguyễn Thảo &quot;phải l&ograve;ng&quot;&nbsp; Đ&agrave; Lạt bởi vị ngọt thanh thanh, hương thơm thoang thoảng của những quả hồng trong vườn th&igrave; đừng do dự nữa, h&atilde;y đến Đ&agrave; Lạt ngay đi th&ocirc;i.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Mua-hong-den-roi-co-ai-thoang-thay-nho-da-lat-chua-26-10-2018-3-1.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>Đ&agrave; Lạt ơi, bỗng thấy nhớ mi rồi!</em></p>\r\n\r\n<h2><strong>Hướng dẫn đường đi đến vườn hồng:</strong></h2>\r\n\r\n<p>Để đến được vườn hồng xinh xắn ở Đ&agrave; Lạt, bạn h&atilde;y đi hướng Trại M&aacute;t đường Khe Sanh, đồi Mimosa, c&oacute; đi qua bệnh viện Ho&agrave;n Mỹ đi thẳng đến cafe F Land, chạy th&ecirc;m một đoạn đến ch&acirc;n đồi Mimosa, m&ugrave;a n&agrave;y đang m&ugrave;a hồng c&aacute;c bạn đi ra v&ugrave;ng ngoại th&agrave;nh ở c&aacute;c đồi trồng rất nhiều, tr&aacute;i đang rất sai lu&ocirc;n.</p>\r\n\r\n<p>M&igrave;nh đi hướng hồ Tuyền L&acirc;m cũng rất nhiều, chỉ c&oacute; hướng đi M&ecirc; Linh, Hoa Sơn Điền Trang th&igrave; &iacute;t th&ocirc;i. Vườn n&agrave;y m&igrave;nh định v&agrave;o mua v&agrave; xin chụp ảnh, nhưng anh chủ đ&atilde; b&aacute;n cả vườn rồi, n&ecirc;n m&igrave;nh chỉ chụp th&ocirc;i v&agrave; cam đoan l&agrave; kh&ocirc;ng h&aacute;i quả n&agrave;o đ&acirc;u.<br />\r\nGi&aacute; hồng tầm 12-15k/kg, v&agrave; m&igrave;nh cũng mới biết l&agrave; người b&aacute;n bỏ hồng v&agrave;o t&uacute;i buộc k&iacute;n để tầm 1 tuần từ l&uacute;c h&aacute;i, ăn kh&ocirc;ng bị ch&aacute;t c&aacute;c bạn ơi. T&uacute;i tầm 3-5kg. Tụi m&igrave;nh nh&igrave;n vườn hồng m&ugrave;a n&agrave;y như vườn ch&ocirc;m ch&ocirc;m &aacute;, đang rất nhiều quả. Trước khi đi m&igrave;nh cũng xem c&aacute;c vườn&nbsp;<a href=\"http://www.vietnambooking.com/du-lich\"><em><strong>du lịch</strong></em></a>&nbsp;tham quan nhưng &iacute;t tr&aacute;i v&agrave; kh&ocirc;ng đẹp lắm. C&aacute;c bạn c&oacute; đi th&igrave; xin chụp ảnh m&igrave;nh cẩn thận t&iacute;, kh&ocirc;ng h&aacute;i, bẻ c&agrave;nh, c&oacute; thể mua về l&agrave; được. Người Đ&agrave; Lạt dễ thương lắm!&nbsp;</p>', '2019-12-19 09:09:09', 1, 7, 'mua-hong-den-roi-co-ai-thoang-thay-nho-da-lat-chua.1576746549.jpg', 'mua-hong-den-roi-co-ai-thoang-thay-nho-da-lat-chua.438.html'),
(9, 'Du lịch Ninh Bình 2 ngày 1 đêm chỉ 900.000Đ – Chuyện tưởng khó mà dễ vô cùng', 'Ninh Bình có Tràng An, có chùa Bái Đính, hang Múa, Động Am Tiên, Tam Cốc, Bích Động… và cũng là một điểm đến mơ ước của rất nhiều người.', '<h2><strong>Ng&agrave;y đầu ti&ecirc;n: H&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; Ninh B&igrave;nh bắt đầu từ Ch&ugrave;a B&aacute;i Đ&iacute;nh</strong></h2>\r\n\r\n<p>Bọn m&igrave;nh xuất ph&aacute;t ở H&agrave; Nội l&uacute;c 7h s&aacute;ng v&agrave; đi thẳng đến homestay N&uacute;i Đ&iacute;nh l&uacute;c 9h30p (tr&ecirc;n đường đi bọn m&igrave;nh c&oacute; dừng lại để tr&uacute; mưa v&agrave; cũng l&agrave; để nghỉ ngơi). Homestay N&uacute;i Đ&iacute;nh c&aacute;ch ch&ugrave;a B&aacute;i Đ&iacute;nh 800m, nh&oacute;m m&igrave;nh d&ugrave; c&oacute; 6 người nhưng quyết định đặt hẳn ph&ograve;ng 4 giường to rộng r&atilde;i để nghỉ ngơi cho thoải m&aacute;i. May mắn đặt được ph&ograve;ng gi&aacute; ưu đ&atilde;i chỉ c&oacute; 400.000Đ/ng&agrave;y. Đến homestay bọn m&igrave;nh nghỉ ngơi, cất h&agrave;nh l&iacute; rồi ăn trưa ở đ&acirc;y lu&ocirc;n, homestay c&oacute; nh&agrave; h&agrave;ng với nhiều ngon lắm, bạn c&oacute; thể đặt trước hoặc đến nơi rồi gọi m&oacute;n cũng được.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-4.jpg\" style=\"height:720px; width:960px\" /><em>Ch&ugrave;a B&aacute;i Đ&iacute;nh&nbsp;l&agrave; điểm đến đầu ti&ecirc;n trong chuyến du lịch Ninh B&igrave;nh của bọn m&igrave;nh.</em></p>\r\n\r\n<p>Chiều khoảng 2h, bọn m&igrave;nh xuất ph&aacute;t đi tham quan ch&ugrave;a B&aacute;i Đ&iacute;nh, ng&ocirc;i ch&ugrave;a lớn nhất Đ&ocirc;ng Nam &Aacute; với kiến tr&uacute;c độc đ&aacute;o. Đến ch&ugrave;a B&aacute;i Đ&iacute;nh bạn c&oacute; thể đi xe điện đến cổng ch&ugrave;a chi ph&iacute; khoảng 30.000Đ/lượt hoặc đi bộ. Nhưng theo m&igrave;nh th&igrave; đi bộ cũng được v&igrave; đoạn đường cũng kh&ocirc;ng qu&aacute; xa, hơn nữa xe điện chỉ đến cổng ch&ugrave;a đoạn đường c&ograve;n lại bạn vẫn phải đi bộ. Sau khi dạo chơi, chụp ảnh, tham quan ở khu vực ch&aacute;nh điện, bọn m&igrave;nh mua v&eacute; tham quan th&aacute;p. Từ tr&ecirc;n th&aacute;p của ch&ugrave;a, bạn c&oacute; thể ngắm to&agrave;n cảnh ch&ugrave;a B&aacute;i Đ&iacute;nh, cảnh đẹp tuyệt vời.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-2.jpg\" style=\"height:960px; width:630px\" /></p>\r\n\r\n<p><em>Đ&acirc;y l&agrave; ngọn th&aacute;p to nhất ở ch&ugrave;a B&aacute;i Đ&iacute;nh.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-3.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>View từ th&aacute;p đẹp lung linh lu&ocirc;n.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-1.jpg\" style=\"height:720px; width:960px\" /><em>Cổng ch&agrave;o Tr&agrave;ng An.</em></p>\r\n\r\n<p>4h chiều bọn m&igrave;nh chia tay ch&ugrave;a B&aacute;i Đ&iacute;nh về lại homestay nghỉ ngơi v&agrave; c&oacute; &yacute; định l&agrave;m một bữa tiệc BBQ, nhưng &ldquo;người t&iacute;nh kh&ocirc;ng bằng trời t&iacute;nh&rdquo; trời mưa to n&ecirc;n tiệc BBQ cũng dời lại lu&ocirc;n. Bọn m&igrave;nh về homestay nghỉ ngơi v&agrave; ăn tối ở đ&oacute; lu&ocirc;n. Kết th&uacute;c 1 ng&agrave;y mưa gi&oacute; ở Ninh B&igrave;nh.</p>\r\n\r\n<h2><strong>Ng&agrave;y thứ hai: Kh&aacute;m ph&aacute; tuyệt cảnh Tr&agrave;ng An, Hang M&uacute;a, Động Am Ti&ecirc;n</strong></h2>\r\n\r\n<p>Buổi s&aacute;ng nh&oacute;m m&igrave;nh dậy thật sớm để chuẩn bị đi ăn s&aacute;ng rồi l&ecirc;n đường kh&aacute;m ph&aacute; Tr&agrave;ng An. Trước đ&acirc;y m&igrave;nh vẫn th&iacute;ch được cảm gi&aacute;c ngồi thuyền ngắm cảnh Tr&agrave;ng An, lần n&agrave;y được trải nghiệm cảm gi&aacute;c thật tuyệt vời. Mỗi thuyền chỉ chở được từ 4 đến 5 người. Ở Tr&agrave;ng An c&oacute; 3 tuyến tham quan, bạn c&oacute; thể tham khảo v&agrave; chọn một tuyến tham quan y&ecirc;u th&iacute;ch nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-5.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Đến Tr&agrave;ng An điều m&igrave;nh th&iacute;ch nhất vẫn l&agrave; ngồi thuyền.</em></p>\r\n\r\n<p>+ Tuyến 1: Bến thuyền &ndash; Đền Tr&igrave;nh &ndash; Hang Tối &ndash; Hang S&aacute;ng &ndash; Hang Nấu Rượu &ndash; Đền Trần &ndash; Hang Ba Giọt &ndash; Hang Seo &ndash; Hang Sơn Dương &ndash; Phủ Khống &ndash; Ch&ugrave;a B&aacute;o Hiếu &ndash; Hang Khống &ndash; Hang Trần &ndash; Hang Quy Hậu &ndash; quay về bến thuyền (thời gian 3,5 tiếng)</p>\r\n\r\n<p>+ Tuyến 2: Bến thuyền &ndash; Hang Lấm &ndash; Hang Vạng &ndash; Hang Th&aacute;nh Trượt &ndash; Đền Suối Ti&ecirc;n &ndash; Hang Đại &ndash; H&agrave;nh Cung Vũ L&acirc;m &ndash; Phim trường Kong (l&agrave;ng thổ d&acirc;n) &ndash; quay về bến thuyền (thời gian 2,5 tiếng)</p>\r\n\r\n<p>+ Tuyến 3: Bến thuyền &ndash; Đền Tr&igrave;nh &ndash; Hang M&acirc;y &ndash; Suối Ti&ecirc;n &ndash; Hang Địa Linh &ndash; Hang Đại &ndash; H&agrave;nh Cung Vũ L&acirc;m &ndash; phim trường Kong (l&agrave;ng thổ d&acirc;n) &ndash; quay về bến thuyền (thời gian 3 tiếng)</p>\r\n\r\n<p>Nh&oacute;m m&igrave;nh thống nhất chọn tuyến 2 để tham quan, thời gian tham quan của tuyến 2 kh&aacute; hợp l&iacute;, c&aacute;c điểm đi qua cũng th&uacute; vị nữa. Kết th&uacute;c h&agrave;nh tr&igrave;nh l&ecirc;nh đ&ecirc;nh chi&ecirc;m ngưỡng cảnh đẹp h&ugrave;ng vĩ của Tr&agrave;ng An, 10h bọn m&igrave;nh xuất ph&aacute;t đến Động Am Ti&ecirc;n. Động Am Ti&ecirc;n được v&iacute; như Tuyệt T&igrave;nh Cốc, cảnh ở đ&acirc;y hơi giống với phim trường, nhưng theo m&igrave;nh nếu bạn c&oacute; lịch tr&igrave;nh tham quan nhiều hơn ở Ninh B&igrave;nh bạn c&oacute; thể bỏ qua Động Am Ti&ecirc;n v&igrave; ở đ&acirc;y cũng kh&aacute; buồn.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-8.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Bến thuyền ở Tr&agrave;ng An.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-6.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-7.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-9.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>L&agrave;ng thổ d&acirc;n da đỏ trong phim Kong - Đảo đầu l&acirc;u n&egrave;.</em></p>\r\n\r\n<p>Ngắm cảnh Động Am Ti&ecirc;n một t&iacute; rồi nh&oacute;m m&igrave;nh t&igrave;m qu&aacute;n ăn, nghỉ trưa để tiếp tục tham quan Hang M&uacute;a. 13h bọn m&igrave;nh xuất ph&aacute;t đến Hang M&uacute;a (c&aacute;ch Tr&agrave;ng An khoảng 4km), để l&ecirc;n đến đỉnh Hang M&uacute;a, bạn phải vượt qua 500 bậc thang. D&ugrave; đoạn đường kh&aacute; d&agrave;i nhưng cảnh đẹp đường l&ecirc;n Hang M&uacute;a thật sự đ&aacute;ng để bạn chinh phục, đứng ở g&oacute;c n&agrave;o cũng c&oacute; những bức ảnh đẹp. Tham quan, chụp ảnh thoải m&aacute;i ở Hang M&uacute;a,&nbsp; 16h30 bọn m&igrave;nh bắt đầu quay về homestay để trả ph&ograve;ng, bọn m&igrave;nh thu&ecirc; th&ecirc;m 4h nghỉ ngơi th&ecirc;m 1 ch&uacute;t đảm bảo đủ sức khỏe để 18h l&ecirc;n đường về H&agrave; Nội.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-10.jpg\" style=\"height:720px; width:960px\" /><em>Động Am Ti&ecirc;n (hay c&ograve;n được gọi l&agrave; Tuyệt T&igrave;nh Cốc)&nbsp;- Ở đ&acirc;y hơi buồn một t&iacute; nha.</em></p>\r\n\r\n<p>Kết th&uacute;c chuyến đi, bọn m&igrave;nh vẫn c&ograve;n hơi tiếc v&igrave; bỏ lỡ một bữa tiệc BBQ ngo&agrave;i trời ở homestay v&agrave; chưa được ngắm Hang M&uacute;a l&uacute;c ho&agrave;ng h&ocirc;n. Trước khi c&oacute; chuyến&nbsp;<strong><a href=\"http://www.vietnambooking.com/du-lich\">du lịch gi&aacute; rẻ</a>&nbsp;Ninh B&igrave;nh</strong>&nbsp;2 ng&agrave;y 1 đ&ecirc;m n&agrave;y, m&igrave;nh đ&atilde; được nghe n&oacute;i, ngắm cảnh ho&agrave;ng h&ocirc;n ở đ&acirc;y rất đẹp nhưng v&igrave; thời gian hạn hẹp n&ecirc;n đ&agrave;nh bỏ lỡ. R&uacute;t ra được kinh nghiệm, nếu bạn c&oacute; đi Ninh B&igrave;nh 2 ng&agrave;y th&igrave; n&ecirc;n đến Hang M&uacute;a ng&agrave;y đầu ti&ecirc;n lu&ocirc;n v&agrave; thoải m&aacute;i ngắm cảnh đẹp ở đ&acirc;y nh&eacute;!</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-11.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-12.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-ninhbinh-2n1d-chi900-chuyen-tuong-kho-ma-de-vo-cung-24102018-13.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Cảnh đẹp tuyệt vời tr&ecirc;n Hang M&uacute;a.</em></p>\r\n\r\n<h3><strong>* Tổng thiệt hại sau chuyến du lịch Ninh B&igrave;nh:</strong></h3>\r\n\r\n<p>- Tiền xăng: c&aacute;c xe tự chi (hết khoảng 100.000Đ &ndash; 150.000Đ/2 người)</p>\r\n\r\n<p>- V&eacute; thang m&aacute;y l&ecirc;n h&aacute;p: 300.000Đ/6 người</p>\r\n\r\n<p>- Ăn uống tr&ecirc;n đường đi: 160.000Đ</p>\r\n\r\n<p>- V&eacute; thuyền Tr&agrave;ng An: 1.200.000Đ</p>\r\n\r\n<p>- Ăn cơm trưa: 600.000Đ</p>\r\n\r\n<p>- Gửi xe l&ecirc;n Hang M&uacute;a: 30.000Đ</p>\r\n\r\n<p>- Tiền đồ ăn vặt: 100.000Đ</p>\r\n\r\n<p>- V&eacute; xe l&ecirc;n ch&ugrave;a B&aacute;i Đ&iacute;nh: 45.000Đ</p>\r\n\r\n<p>- Tiền ăn s&aacute;ng: 180.000Đ</p>\r\n\r\n<p>- V&eacute; Hang M&uacute;a: 600.000Đ</p>\r\n\r\n<p>- Tiền 2 bữa ăn ng&agrave;y thứ nhất: 1.400.000Đ</p>\r\n\r\n<p>- Tiền ph&ograve;ng: 550.000Đ (bọn m&igrave;nh ở th&ecirc;m nửa ng&agrave;y)&nbsp;</p>\r\n\r\n<p>=&gt;Tổng chi ti&ecirc;u: 5.165.000Đ chia cho 6 người, mỗi người 860.000Đ + chi ph&iacute; xăng xe nữa l&agrave; 900.000Đ&nbsp;</p>', '2019-12-19 09:08:59', 1, 3, 'du-lich-ninh-binh-2-ngay-1-dem-chi-900000d-chuyen-tuong-kho-ma-de-vo-cung.1576746539.jpg', 'du-lich-ninh-binh-2-ngay-1-dem-chi-900000d-chuyen-tuong-kho-ma-de-vo-cung.487.html'),
(10, 'Bí kíp check in đồi chè Ô Long Sapa', 'Bạn biết không, Du lịch ở Sapa hiện nay đang tồn tại một đồi chè đẹp ngang ngửa Mộc Châu đấy. Không những thế đồi chè này còn đặc biệt ở chỗ chúng được trồng xen kẽ những cây hoa Anh Đào.', '<p><img alt=\"Check in đồi chè Ô Long Sapa\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Bi-kip-check-in-doi-che-o-long-sa-pa-24-10-2018-4.jpg\" style=\"height:575px; width:920px\" /></p>\r\n\r\n<p><em>Đồi ch&egrave; n&agrave;y được trồng&nbsp;xen kẽ những c&acirc;y hoa Anh Đ&agrave;o,</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Bi-kip-check-in-doi-che-o-long-sa-pa-24-10-2018-3.jpg\" style=\"height:573px; width:920px\" /></p>\r\n\r\n<p><em>Những&nbsp;h&agrave;ng c&acirc;y Anh Đ&agrave;o được trồng thẳng h&agrave;ng v&agrave; dọc c&aacute;c lối đi v&agrave;o những luống ch&egrave;, nh&igrave;n từ ph&iacute;a xa cứ ngỡ như những ngọn nến hồng vậy,</em></p>\r\n\r\n<p><img alt=\"Check in đồi chè Ô Long Sapa\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Bi-kip-check-in-doi-che-o-long-sa-pa-24-10-2018-1.jpg\" style=\"height:615px; width:920px\" /></p>\r\n\r\n<p><em>Nếu bạn may mắn đi v&agrave;o h&ocirc;m c&oacute; một ch&uacute;t m&acirc;y tr&agrave;n v&agrave;o, bạn sẽ thấy cả đồi ch&egrave; ẩn hiện trong m&acirc;y v&ocirc; c&ugrave;ng l&atilde;ng mạn v&agrave; phi&ecirc;u bồng lu&ocirc;n.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Bi-kip-check-in-doi-che-o-long-sa-pa-24-10-2018-6.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"Check in đồi chè Ô Long Sapa\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Bi-kip-check-in-doi-che-o-long-sa-pa-24-10-2018-7.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>Đ&acirc;y l&agrave; hai trong số nhiều bức ảnh bọn m&igrave;nh chụp tại đồi ch&egrave;. Đồi ch&egrave; l&uacute;c trời nắng đẹp v&agrave; l&uacute;c ho&agrave;ng h&ocirc;n sẽ l&ecirc;n h&igrave;nh cực đẹp. Chụp ngược s&aacute;ng cũng đẹp phết nhỉ?</em></p>\r\n\r\n<p><img alt=\"Đi du lịch đồi chè ô long ở Sapa\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Bi-kip-check-in-doi-che-o-long-sa-pa-24-10-2018-5.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>Tin vui cho c&aacute;c bạn l&agrave; khoảng thời gian c&acirc;y Anh Đ&agrave;o ra l&aacute; non sẽ k&eacute;o d&agrave;i khoảng một th&aacute;ng mới hết.Vậy n&ecirc;n c&aacute;c bạn ho&agrave;n to&agrave;n c&oacute; đủ thời gian để &quot;khăn gối l&ecirc;n đường&quot; đến đ&acirc;y check in.</em></p>\r\n\r\n<h2><strong>Đồi ch&egrave; &Ocirc; Long Sapa nằm ở vị tr&iacute; n&agrave;o?</strong></h2>\r\n\r\n<p>N&oacute;i n&atilde;y giờ m&igrave;nh vẫn chưa định vị cho c&aacute;c bạn đồi ch&egrave; &quot;chất như nước cất&quot; n&agrave;y nằm ở đ&acirc;y. Thật ra, t&igrave;m đường v&agrave;o đồi ch&egrave; n&agrave;y cũng kh&aacute; gian nan, đ&oacute; cũng l&agrave; l&yacute; do v&igrave; sao &quot;em n&oacute;&quot; đẹp thế n&agrave;y nhưng lại &iacute;t người biết đến.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/Bi-kip-check-in-doi-che-o-long-sa-pa-24-10-2018-8.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>T&igrave;m đường đến check in đồi ch&egrave; &Ocirc; Long ngay th&ocirc;i!</em></p>\r\n\r\n<p>Đồi ch&egrave;&nbsp;nằm c&aacute;ch trung t&acirc;m chừng 6km dọc theo quốc lộ 4D (tức l&agrave; đường l&ecirc;n Th&aacute;c Bạc hay đ&egrave;o &Ocirc; Qu&yacute; Hồ ấy). Nếu c&aacute;c bạn đi từ thị trấn l&ecirc;n th&igrave; h&atilde;y nh&igrave;n b&ecirc;n tay tr&aacute;i, tầm 6km sẽ tự nhi&ecirc;n thấy. Lối v&agrave;o của đồi ch&egrave; bạn c&oacute; thể t&igrave;m thấy bằng c&aacute;ch hỏi thăm người d&acirc;n ven đường, hoặc để &yacute; b&ecirc;n tay phải c&oacute; c&aacute;i điểm trường mầm non tổ 3 th&igrave; lối đi v&agrave;o ở đối diện c&aacute;i cổng trường lu&ocirc;n.</p>\r\n\r\n<h2><strong>M&aacute;ch nhỏ bạn c&aacute;ch v&agrave;o tham quan đồi ch&egrave;</strong></h2>\r\n\r\n<p>&nbsp;T&igrave;m được đường v&agrave;o rồi nhưng nhiều bạn đến cổng lại phải ra về đấy. V&igrave; &ocirc;ng chủ đồi ch&egrave; l&agrave; người Đ&agrave;i Loan v&agrave; kh&ocirc;ng sống ở đấy. Đồi ch&egrave; được giao cho một gia đ&igrave;nh người H M&ocirc;ng tr&ocirc;ng cửa. Họ c&oacute; nhiệm vụ kh&ocirc;ng cho người lạ v&agrave;o v&igrave; nhiều người ra v&agrave;o tự do sẽ c&oacute; thể l&agrave;m hỏng c&aacute;c luống ch&egrave;.V&igrave; vậy nếu bạn muốn v&agrave;o tham quan đồi ch&egrave; c&oacute; thể &aacute;p dụng c&aacute;ch sau đ&acirc;y của m&igrave;nh.&nbsp;</p>\r\n\r\n<p>C&aacute;ch của m&igrave;nh rất đơn giản, trong gia đ&igrave;nh người H M&ocirc;ng hiện tại c&oacute; một cặp vợ chồng sống c&ugrave;ng bố mẹ v&agrave; 4 đứa con nhỏ. Trước khi v&agrave;o đồi ch&egrave; m&igrave;nh đ&atilde; mua sẵn v&agrave;i ba thức qu&agrave; nhỏ như: một lốc sữa&nbsp;cho lũ trẻ, một &iacute;t hoa quả biếu &ocirc;ng b&agrave; cụ người H M&ocirc;ng. Khi cụ b&agrave; ra mở cửa th&igrave; đừng n&oacute;i l&agrave; cho ch&aacute;u v&agrave;o đồi ch&egrave; ch&aacute;u biếu cụ c&aacute;i n&agrave;y. M&agrave; n&ecirc;n bảo l&agrave; cho ch&aacute;u v&agrave;o chơi với mấy đứa trẻ, ch&aacute;u c&oacute; qu&agrave; cho ch&uacute;ng ạ.Thế l&agrave; họ cho v&agrave;o, m&igrave;nh tặng qu&agrave; b&aacute;nh cho c&aacute;c b&eacute;, cho ch&uacute;ng 10,000 - 20.000VNĐ&nbsp;xong m&igrave;nh n&oacute;i với b&agrave; cụ người M&ocirc;ng l&agrave;: &quot;Wow, đồi ch&egrave; đẹp qu&aacute;, b&agrave; cho ch&aacute;u v&agrave;o chơi chụp ảnh 1 t&yacute; nh&eacute;&quot;. Thế l&agrave; b&agrave; đồng &yacute; ngay.</p>\r\n\r\n<p>Đơn giản việc l&agrave;m n&agrave;y vừa c&oacute; thể gi&uacute;p bạn v&agrave;o đồi ch&egrave; check in m&agrave; c&ograve;n c&oacute; được những ph&uacute;t gi&acirc;y vui chơi c&ugrave;ng lũ trẻ. Đến chơi nh&agrave; mua ch&uacute;t qu&agrave; th&igrave; cũng hợp l&yacute; m&agrave;, đ&uacute;ng kh&ocirc;ng? M&igrave;nh&nbsp;từng gặp mấy nh&oacute;m đi&nbsp;<a href=\"http://www.vietnambooking.com/du-lich\"><strong><em>du lịch</em></strong></a>&nbsp;nh&igrave;n chất qu&yacute; tộc lắm, xin v&agrave;o kh&ocirc;ng được, b&egrave;n đưa thẳng 500.000VNĐ với th&aacute;i độ &quot;tiền đ&acirc;y, mở cửa đi&quot; v&agrave; b&agrave; ấy ko th&egrave;m cho v&agrave;o đấy. V&igrave; vậy h&atilde;y giữ th&aacute;i độ lịch thiệp, &ocirc;n h&ograve;a bạn nha.</p>', '2019-10-14 02:15:11', 1, 4, 'yWNRWp3huSJN4GHHp53UII1NuHZwggIU7ySc2yWI.jpeg', ''),
(11, 'Đà Lạt – Đi để tận hưởng & tìm sự bình yên đích thực cho bản thân', 'Đà Lạt đẹp, chắc rồi, nhưng sao mà bằng mùa thu Úc, mùa thu Jeju. Cũng chẳng sao, đôi khi yêu một điều gì đó thì không thể lý giải bằng lời.', '<p>Từ khi biết Đ&agrave; Lạt đến nay cũng đ&atilde; hơn 10 năm v&agrave; dường như năm n&agrave;o t&ocirc;i cũng đến Đ&agrave; Lạt, mỗi năm đến một m&ugrave;a kh&aacute;c nhau để cảm nhận v&agrave; để y&ecirc;u th&ecirc;m th&agrave;nh phố xinh đẹp n&agrave;y.&nbsp;<strong><a href=\"https://www.vietnambooking.com/du-lich/tour-du-lich/du-lich-da-lat.html\">Du lịch Đ&agrave; Lạt</a></strong>&nbsp;trong những năm gần đ&acirc;y đ&atilde; thay đổi rất nhiều, nhiều điểm tham quan hơn, cảnh đẹp hơn v&agrave; thật sự cứ mỗi lần đến nơi n&agrave;y, t&ocirc;i lại thấy Đ&agrave; Lạt sao thật kh&aacute;c, thật đẹp biết chừng n&agrave;o.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/06/b-1.jpg\" style=\"height:inherit\" /></p>\r\n\r\n<p><em>Đến Đ&agrave; Lạt đừng qu&ecirc;n ngắm b&igrave;nh minh sớm . @&nbsp;Lệ Thủy B&ugrave;i</em></p>\r\n\r\n<p><a id=\"type1\" name=\"type1\">Di chuyển l&ecirc;n Đ&agrave; Lạt</a>&nbsp;t&ocirc;i hay chọn cho m&igrave;nh chuyến xe v&agrave;o buổi đ&ecirc;m để c&oacute; thể kịp ngắm b&igrave;nh minh cao nguy&ecirc;n l&uacute;c rạng s&aacute;ng. Vừa đến Đ&agrave; Lạt l&uacute;c 5 giờ s&aacute;ng, c&aacute;i se lạnh buổi s&aacute;ng sớm của Đ&agrave; Lạt t&ocirc;i đ&atilde; qu&aacute; quen nhưng lần n&agrave;o cũng c&oacute; ch&uacute;t &ldquo;sởn da g&agrave;&rdquo;. C&aacute;i se lạnh của Đ&agrave; lạt n&oacute; khiến bạn m&ocirc;ng lung để thả hồn m&igrave;nh theo 1 t&aacute;ch tr&agrave; atiso n&oacute;ng, một ly sữa đậu n&agrave;nh ấm &aacute;p hoặc một ly c&agrave; ph&ecirc; của v&ugrave;ng cao nguy&ecirc;n để ngắm cảnh th&agrave;nh phố b&igrave;nh y&ecirc;n trong sương mờ, v&agrave; để cảm nhận về ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/06/h.jpg\" style=\"height:inherit\" /></p>\r\n\r\n<p>Đ&agrave; Lạt b&igrave;nh y&ecirc;n lắm, b&igrave;nh y&ecirc;n từ tr&ecirc;n phố thị đến những khu vườn nằm c&aacute;ch xa th&agrave;nh phố. Đến với&nbsp;<a id=\"type2\" name=\"type2\">Secret Garden</a>, bạn sẽ tin lời t&ocirc;i, tin v&agrave;o một Đ&agrave; Lạt b&igrave;nh y&ecirc;n đẹp nhưng những bức tranh. Đứng trước mặt hồ rộng lớn, ngắm nh&igrave;n đồi th&ocirc;ng trước mặt, t&ocirc;i thấy m&igrave;nh thật nhỏ b&eacute;, tho&aacute;ng thấy ch&uacute;t c&ocirc; đơn v&agrave; lạnh lẽo. Bạn c&oacute; thể chọn cho m&igrave;nh bất k&igrave; vị tr&iacute; n&agrave;o t&ugrave;y th&iacute;ch, thử một lần nh&igrave;n l&ecirc;n những t&aacute;n l&aacute; của rừng th&ocirc;ng, len lỏi qua những khe l&aacute; v&agrave; t&igrave;m thấy bầu trời v&agrave; để nghĩ một ch&uacute;t về cuộc sống của ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/06/f.jpg\" style=\"height:inherit\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/06/d.jpg\" style=\"height:inherit\" /></p>\r\n\r\n<p>&nbsp;<em>Secret Garden đ&atilde; l&agrave; c&aacute;i t&ecirc;n &ldquo;g&acirc;y chao đảo&rdquo; cộng đồng mạng trong suốt những th&aacute;ng cuối năm 2017.&nbsp;@&nbsp;Lệ Thủy B&ugrave;i</em></p>\r\n\r\n<p>Nhiều người n&oacute;i, Đ&agrave; Lạt l&agrave; th&agrave;nh phố d&agrave;nh cho những đ&ocirc;i lứa c&oacute; những khoảnh khắc l&atilde;ng mạn b&ecirc;n nhau, nhưng với t&ocirc;i Đ&agrave; Lạt l&agrave; th&agrave;nh phố d&agrave;nh cho những người c&ocirc; đơn. C&ocirc; đơn để thấy cuộc sống thật y&ecirc;n b&igrave;nh v&agrave; nhẹ nh&agrave;ng. Đi rong ruổi ở Secret Garden, bạn sẽ thấy đ&acirc;y kh&ocirc;ng chỉ l&agrave; một cảnh đẹp, nơi để sống ảo thả ga m&agrave; c&ograve;n l&agrave; một nơi đầy cuốn h&uacute;t để bạn c&oacute; thể bắt trọn những khoảnh khắc kh&oacute; qu&ecirc;n trong đời.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/06/e.jpg\" style=\"height:inherit\" /></p>\r\n\r\n<p><em>Rong ruổi ở Secret Garden -&nbsp;bắt trọn những khoảnh khắc kh&oacute; qu&ecirc;n trong đời.@ L&ecirc; Thủy Bủi</em></p>\r\n\r\n<p>Lại một lần nữa t&ocirc;i gh&eacute; thăm&nbsp;<a id=\"type3\" name=\"type3\">L&agrave;ng C&ugrave; Lần</a>&nbsp;d&ugrave; c&oacute; hơi xa trung t&acirc;m th&agrave;nh phố nhưng nơi n&agrave;y với t&ocirc;i l&agrave; rất đẹp, đ&aacute;ng để bạn d&agrave;nh trọn cả một ng&agrave;y. Nơi n&agrave;y d&ugrave; đ&atilde; bị &ldquo;du lịch h&oacute;a&rdquo; nhiều năm trở lại đ&acirc;y nhưng vẫn c&ograve;n rất hấp dẫn. Những căn nh&agrave; s&agrave;n nằm giữa khu rừng th&ocirc;ng rộng lớn l&agrave; nơi gi&uacute;p bạn hiểu hơn về văn h&oacute;a, cuộc sống, sinh hoạt của đồng b&agrave;o d&acirc;n tộc ở v&ugrave;ng n&uacute;i T&acirc;y Nguy&ecirc;n. H&atilde;y thử tưởng tượng, chọn một g&oacute;c &ldquo;view&rdquo; thật đẹp, vừa nh&acirc;m nhi rượu cần đ&uacute;ng điệu v&ugrave;ng đất T&acirc;y Nguy&ecirc;n vừa thưởng thức một miếng thịt rừng nướng rồi ngắm cảnh, rồi tr&ograve; chuyện mới thật th&uacute; vị l&agrave;m sao.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/06/w.jpg\" style=\"height:inherit\" /></p>\r\n\r\n<p><em>L&agrave;ng C&ugrave; Lần - Cảm nhận sự mộc mạc của Đ&agrave; Lạt</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/06/m.jpg\" style=\"height:inherit\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/06/n.jpg\" style=\"height:inherit\" /></p>\r\n\r\n<p><em>Sống chậm, b&igrave;nh y&ecirc;n l&agrave; cảm gi&aacute;c L&agrave;ng C&ugrave; Lần c&oacute; thể mang đến cho bạn</em></p>\r\n\r\n<p>Đi đ&acirc;u rồi cũng về với trung t&acirc;m th&agrave;nh phố để ngắm nh&igrave;n mặt&nbsp;<a id=\"type4\" name=\"type4\">Hồ Xu&acirc;n Hương</a>&nbsp;thanh b&igrave;nh, mờ ảo trong sương mờ của buổi s&aacute;ng sớm. Đừng vội đi tham quan nơi n&agrave;o kh&aacute;c khi bạn chưa thưởng thức một ly c&agrave; ph&ecirc; cao nguy&ecirc;n. H&atilde;y chọn cho m&igrave;nh một qu&aacute;n c&agrave; ph&ecirc; kh&ocirc;ng cần sang trọng, kh&ocirc;ng cần c&oacute; decor đẹp, chỉ cần view đẹp v&agrave; c&agrave; ph&ecirc; ngon l&agrave; được. Bằng c&aacute;ch n&agrave;y, bạn c&oacute; thể ngắm Đ&agrave; Lạt một c&aacute;ch thật trọn vẹn, để cảm nhận Đ&agrave; Lạt đẹp v&agrave; b&igrave;nh y&ecirc;n thế n&agrave;o.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/06/p.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/06/t.jpg\" style=\"height:413px; width:550px\" /></p>\r\n\r\n<p><em>Hồ Xu&acirc;n Hương - Nơi bạn tĩnh lặng nh&igrave;n cuộc sống</em></p>\r\n\r\n<p>Đ&agrave; Lạt l&agrave; th&agrave;nh phố của những sắc hoa rực rỡ, lu&ocirc;n được nhiều bạn trẻ t&igrave;m kiếm, nhưng t&ocirc;i kh&ocirc;ng chọn cho m&igrave;nh những sắc hoa đ&oacute;, t&ocirc;i chọn một g&oacute;c kh&aacute;c của Đ&agrave; Lạt v&agrave; cảm nhận. Mỗi khi đến Đ&agrave; Lạt t&ocirc;i th&iacute;ch đi bộ dạo quanh những con dốc ở Đ&agrave; Lạt, đi xe đạp quanh hồ Xu&acirc;n Hương, ngồi một qu&aacute;n vỉa h&egrave; thưởng thức m&oacute;n b&aacute;nh tr&aacute;ng trộn c&ograve;n n&oacute;ng hổi, một ly sữa đậu n&agrave;nh&hellip; thật b&igrave;nh dị l&agrave;m sao.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/06/r.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/06/q.jpg\" style=\"height:inherit\" /></p>\r\n\r\n<p><em>Đến Đ&agrave; Lạt để t&igrave;m thấy sự b&igrave;nh y&ecirc;n</em></p>\r\n\r\n<p>Nhiều người&nbsp;<a href=\"http://www.vietnambooking.com/du-lich\"><strong><em>du lịch</em></strong></a>&nbsp;Đ&agrave; Lạt để sống ảo, để vui chơi, hoặc c&oacute; thể để tham quan, nghỉ m&aacute;t. Với t&ocirc;i t&igrave;m đến Đ&agrave; Lạt l&agrave; để t&igrave;m về chốn b&igrave;nh y&ecirc;n, khoảng lặng trong cuộc sống v&agrave; để biết m&igrave;nh đang cần g&igrave;. T&ocirc;i y&ecirc;u Đ&agrave; Lạt nhờ những điều thật b&igrave;nh dị, đ&oacute; ch&iacute;nh l&agrave; những m&oacute;n ăn vặt b&igrave;nh d&acirc;n, con dốc, những lối m&ograve;n, những khu rừng th&ocirc;ng r&igrave; r&agrave;o ng&agrave;y đ&ecirc;m v&agrave; nhất l&agrave; những lo&agrave;i hoa đua nhau khoe sắc. Đ&oacute; l&agrave; l&iacute; do v&igrave; sao t&ocirc;i sẵn s&agrave;ng đến Đ&agrave; Lạt bất k&igrave; khi n&agrave;o t&ocirc;i th&iacute;ch v&agrave; trong t&ocirc;i chưa bao giờ biết ch&aacute;n th&agrave;nh phố. T&ocirc;i tin bạn cũng vậy, bạn cũng sẽ y&ecirc;u Đ&agrave; Lạt theo c&aacute;i c&aacute;ch m&agrave; t&ocirc;i y&ecirc;u th&agrave;nh phố n&agrave;y &ndash; th&agrave;nh phố của b&igrave;nh y&ecirc;n.</p>\r\n\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\">&nbsp;</div>', '2019-12-19 09:08:16', 1, 7, 'da-lat-di-de-tan-huong-tim-su-binh-yen-dich-thuc-cho-ban-than.1576746496.jpg', 'da-lat-di-de-tan-huong-tim-su-binh-yen-dich-thuc-cho-ban-than.421.html'),
(12, 'Du lịch Huế – Chưa đi chưa biết Huế đẹp và mộng mơ cỡ nào', 'Đã từng đi du lịch ở nhiều nơi, đã vượt qua rất nhiều cung đường nhưng Huế vẫn là điểm đến mà mình thích nhất.', '<p>Đến Huế v&agrave;o những ng&agrave;y cuối th&aacute;ng 6, thời tiết kh&aacute; đẹp, kh&ocirc;ng mưa, kh&ocirc;ng nắng gắt, m&igrave;nh thật sự c&oacute; chuyến&nbsp;<strong>du lịch Huế&nbsp;</strong>2 ng&agrave;y 2 đ&ecirc;m thật trọn vẹn. D&ugrave; l&agrave; lần thứ 2 đến Huế nhưng m&igrave;nh vẫn cảm thấy Huế đẹp v&agrave; thơ mộng như lần đầu. Đ&oacute;n chuyến xe đ&ecirc;m Ho&agrave;ng Long từ Bi&ecirc;n H&ograve;a (Đồng Nai) đi Huế với gi&aacute; 500.000Đ/lượt, m&igrave;nh nghỉ đ&ecirc;m tr&ecirc;n xe v&agrave; thưởng thức b&agrave;i h&aacute;t nhẹ nh&agrave;ng để sẵn s&agrave;ng cho chuyến vi vu c&oacute; đ&ocirc;. M&igrave;nh đến Huế khi mặt trời vừa khuất b&oacute;ng, thời gian di chuyển đến Huế d&ugrave; kh&aacute; l&acirc;u khiến m&igrave;nh cảm thấy hơi mệt nhưng v&igrave; n&ocirc;n nao kh&aacute;m ph&aacute; Huế n&ecirc;n m&igrave;nh quyết định cất h&agrave;nh l&iacute; ở hostel, nghỉ ngơi khoảng 30 ph&uacute;t, m&igrave;nh đi dạo phố đi bộ, tham quan cầu Tr&agrave;ng Tiền về đ&ecirc;m v&agrave; ăn tối.</p>\r\n\r\n<p><img alt=\"Du lịch huế\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-2.jpg\" style=\"height:768px; width:1024px\" /><em>Amy 2 hostel tọa lạc tại một con hẻm nhỏ ngay s&aacute;t phố đi bộ Huế.&nbsp;</em></p>\r\n\r\n<p>N&oacute;i về hostel, m&igrave;nh chọn Amy 2 hostel (10/26 V&otilde; Thị S&aacute;u, Hu&ecirc;́), gi&aacute; 1 ng&agrave;y chỉ c&oacute; 117.000Đ/người (c&oacute; k&egrave;m th&ecirc;m bữa s&aacute;ng). Chỗ n&agrave;y gần phố đi bộ, buổi tối kh&aacute; đ&ocirc;ng đ&uacute;c v&agrave; cũng c&oacute; nhiều người nước ngo&agrave;i lưu tr&uacute;, m&igrave;nh ở ph&ograve;ng dorm v&agrave; thấy rất h&agrave;i l&ograve;ng v&igrave; giường sạch sẽ, ph&ograve;ng ốc cũng khang trang.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-25.jpg\" style=\"height:640px; width:480px\" /></p>\r\n\r\n<p><em>Ph&ograve;ng dorm sạch sẽ v&agrave; cũng kh&aacute; tiện nghi.</em></p>\r\n\r\n<p>Kh&aacute;c với lời đồn &ldquo;Huế buồn&rdquo;, &ldquo;Huế ch&aacute;n lắm&rdquo; v&igrave; kh&ocirc;ng c&oacute; chỗ n&agrave;o th&iacute;ch hợp để vui chơi buổi tối, điều n&agrave;y kh&ocirc;ng đ&uacute;ng đ&acirc;u nha, ở Huế cũng&nbsp;c&oacute; một khu phố đi bộ nhộn nhịp kh&aacute;ch&nbsp;<a href=\"http://www.vietnambooking.com/du-lich\"><strong><em>du lịch</em></strong></a>&nbsp;tham quan, vui chơi, tấp nập v&agrave; kh&ocirc;ng hề buồn ch&uacute;t n&agrave;o. Lạc bước ra d&ograve;ng s&ocirc;ng Hương, m&igrave;nh dừng lại một ch&uacute;t để ngắm cảnh, d&ugrave; l&agrave; ngay trung t&acirc;m th&agrave;nh phố nhưng l&atilde;ng mạn v&agrave; b&igrave;nh y&ecirc;n v&ocirc; c&ugrave;ng. B&ecirc;n d&ograve;ng s&ocirc;ng Hương m&igrave;nh thật sự mới hiểu rằng v&igrave; sao nhiều người lại y&ecirc;u th&iacute;ch cảnh đẹp của th&agrave;nh phố n&agrave;y đến vậy. V&agrave; m&igrave;nh cũng thế!</p>\r\n\r\n<p><img alt=\"Du lịch huế giá rẻ\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-1.jpg\" style=\"height:960px; width:541px\" /></p>\r\n\r\n<p><em>Một du kh&aacute;ch người nước ngo&agrave;i nhiệt t&igrave;nh, vui t&iacute;nh.</em></p>\r\n\r\n<p>Một đ&ecirc;m thật &yacute; nghĩa ở Huế, m&igrave;nh về hostel nghỉ ngơi để sẵn s&agrave;ng sức khỏe cho chuyến thưởng ngoạn cảnh đẹp l&atilde;ng mạn cố đ&ocirc;. Buổi s&aacute;ng thức dậy thật sớm, m&igrave;nh thưởng thức bữa s&aacute;ng do chủ homestay chi&ecirc;u đ&atilde;i rồi thu&ecirc; một chiếc xe m&aacute;y l&ecirc;n đường kh&aacute;m ph&aacute; Huế. M&igrave;nh đến Kinh Th&agrave;nh Huế, tham quan Kỳ Đ&agrave;i, cửa Ngọ M&ocirc;n, dạo một v&ograve;ng tham quan Đại Nội. Khu&ocirc;n vi&ecirc;n Kinh Th&agrave;nh Huế kh&aacute; rộng, để tham quan hết c&oacute; thể bạn sẽ mất hết một ng&agrave;y đấy, nhưng để tiết kiệm thời gian m&igrave;nh chỉ tham quan một số điểm th&ocirc;i.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-3.jpg\" style=\"height:668px; width:960px\" /></p>\r\n\r\n<p><img alt=\"Đi du lịch huế\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-5.jpg\" style=\"height:668px; width:960px\" /><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-4.jpg\" style=\"height:668px; width:960px\" /></p>\r\n\r\n<p><em>Buổi s&aacute;ng đầu ti&ecirc;n ở Huế, tham quan Đại Nội l&agrave; tuyệt nhất.</em></p>\r\n\r\n<p>Chia tay Kinh Th&agrave;nh Huế, Ch&ugrave;a Thi&ecirc;n Mụ l&agrave; điểm dừng ch&acirc;n tiếp theo ở cố đ&ocirc; Huế. Với m&igrave;nh, ch&ugrave;a Thi&ecirc;n Mụ l&agrave; một ng&ocirc;i ch&ugrave;a đẹp, kh&ocirc;ng gian rộng lớn v&agrave; đặc biệt từ vị tr&iacute; n&agrave;y ngắm cảnh s&ocirc;ng Hương l&agrave; đẹp nhất. Từ ch&ugrave;a Thi&ecirc;n Mụ, m&igrave;nh lại đến tham quan quan ch&ugrave;a Huyền Kh&ocirc;ng, d&ugrave; kh&ocirc;ng nổi tiếng bằng ch&ugrave;a Thi&ecirc;n Mụ nhưng ở đ&acirc;y cũng đẹp, trang nghi&ecirc;m, bạn c&oacute; thể vừa tham quan, vừa tha hồ chụp ảnh sống ảo. Sau khi nghe một số người bạn vừa quen ở Huế, m&igrave;nh được giới thiệu đến tham quan nh&agrave; thờ Phủ Cam. Kiến tr&uacute;c nh&agrave; thờ theo lối kiến tr&uacute;c cổ điển v&agrave; đẹp kh&ocirc;ng thua k&eacute;m g&igrave; với ch&ugrave;a Thi&ecirc;n Mụ.</p>\r\n\r\n<p><img alt=\"Du lịch huế\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-7.jpg\" style=\"height:960px; width:668px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-22.jpg\" style=\"height:668px; width:960px\" /></p>\r\n\r\n<p><em>Ch&ugrave;a Thi&ecirc;n Mụ - Biểu tượng của Huế.</em></p>\r\n\r\n<p><img alt=\"Du lịch ở huế\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-23.jpg\" style=\"height:960px; width:659px\" /></p>\r\n\r\n<p><em>Ch&ugrave;a Huyền Kh&ocirc;ng - Chụp ảnh sống ảo ở đ&acirc;y l&agrave; chuẩn nhất.</em>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-11.jpg\" style=\"height:960px; width:668px\" /></p>\r\n\r\n<p><em>Kiến tr&uacute;c độc đ&aacute;o của nh&agrave; thờ Phủ Cam.</em></p>\r\n\r\n<p>Trường Quốc Học Huế l&agrave; điểm đến tiếp theo của m&igrave;nh, ng&ocirc;i trường được sơn m&agrave;u sắc hết sức ấn tượng. Đ&acirc;y l&agrave; lần đầu m&igrave;nh đến với trường Quốc Học v&agrave; kiến tr&uacute;c, m&agrave;u sắc khiến m&igrave;nh thật sự bất ngờ. V&igrave; tham quan trường v&agrave;o những ng&agrave;y h&egrave; n&ecirc;n vắng b&oacute;ng sinh vi&ecirc;n, khung cảnh kh&aacute; buồn, nhưng b&ugrave; lại cảnh đẹp lắm nha. M&igrave;nh th&iacute;ch nhất l&agrave; những d&atilde;y ghế của trường, chọn một g&oacute;c ri&ecirc;ng, m&igrave;nh chi&ecirc;m ngưỡng ng&ocirc;i trường độc đ&aacute;o n&agrave;y v&agrave; bổng nhớ về một thời sinh vi&ecirc;n.</p>\r\n\r\n<p><img alt=\"Đi du lịch huế giá rẻ\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-13.jpg\" style=\"height:960px; width:668px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-12.jpg\" style=\"height:960px; width:668px\" /></p>\r\n\r\n<p><em>M&agrave;u đỏ ấn tượng ở trường Quốc Học Huế.&nbsp;</em></p>\r\n\r\n<p>Kết th&uacute;c ng&agrave;y đầu ti&ecirc;n kh&aacute;m ph&aacute; cố đ&ocirc;, m&igrave;nh lại dạo bờ s&ocirc;ng Hương, đi du thuyền vừa nghe b&agrave;i ca Huế vừa thưởng ngoạn cảnh đẹp s&ocirc;ng Hương về đ&ecirc;m. L&atilde;ng mạn v&agrave; b&igrave;nh y&ecirc;n đến lạ!</p>\r\n\r\n<p>Ng&agrave;y cuối c&ugrave;ng ở Huế đ&uacute;ng l&agrave; một ng&agrave;y nắng đẹp, m&igrave;nh chọn cầu ng&oacute;i Thanh To&agrave;n để tham quan, d&ugrave; hơi xa trung t&acirc;m th&agrave;nh phố nhưng cảnh ở đ&acirc;y đẹp, b&igrave;nh y&ecirc;n lắm. Cầu ng&oacute;i Thanh To&agrave;n c&oacute; kiến tr&uacute;c kh&aacute; giống với Ch&ugrave;a Cầu ở Hội An nhưng đẹp v&agrave; thơ mộng, đ&uacute;ng chất l&agrave;ng qu&ecirc; Huế lu&ocirc;n. M&igrave;nh may mắn gặp được 2 em g&aacute;i Huế xinh xắn, dễ thương v&ocirc; c&ugrave;ng, đặc biệt l&agrave; chụp ảnh c&oacute; t&acirc;m n&ecirc;n v&ocirc; t&igrave;nh c&oacute; được mấy tấm h&igrave;nh đẹp.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-14.jpg\" style=\"height:668px; width:960px\" /><em>Cầu ngối Thanh To&agrave;n c&oacute; kiến tr&uacute;c kh&aacute; giống ch&ugrave;a Cầu ở Hội An.</em></p>\r\n\r\n<p><img alt=\"du lịch huế\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-19.jpg\" style=\"height:960px; width:668px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-16.jpg\" style=\"height:960px; width:668px\" /></p>\r\n\r\n<p><em>Cảnh đẹp khu&ocirc;n vi&ecirc;n gần cầu ng&oacute;i Thanh To&agrave;n.</em></p>\r\n\r\n<p>Rời cầu ng&oacute;i Thanh To&agrave;n, m&igrave;nh đến với lăng Khải Định &ndash; lăng tẩm đẹp nhất ở Huế. Trước khi c&oacute; chuyến du lịch Huế, m&igrave;nh c&oacute; nghe n&oacute;i nhiều đến những lăng tẩm ở đ&acirc;y kh&aacute; buồn v&agrave; kh&ocirc;ng c&oacute; g&igrave; th&uacute; vị. Nhưng, đến với lăng Khải Định m&igrave;nh thấy lời đồn cũng chỉ l&agrave; lời đồn. Kiến tr&uacute;c của lăng Khải Định cổ k&iacute;nh, trang nghi&ecirc;m v&agrave; theo m&igrave;nh du lịch Huế m&agrave; kh&ocirc;ng đến nơi n&agrave;y th&igrave; thật l&agrave; tiếc.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-21.jpg\" style=\"height:960px; width:668px\" /></p>\r\n\r\n<p><em>Lăng Khải Định.</em></p>\r\n\r\n<p>Tạm kh&eacute;p lại chuyến độc h&agrave;nh đến Huế, m&igrave;nh gh&eacute; qua đồi Vọng Cảnh ngắm d&ograve;ng s&ocirc;ng Hương thơ mộng trước khi về hostel để trả ph&ograve;ng. Để b&igrave;nh chọn nơi n&agrave;o b&igrave;nh y&ecirc;n v&agrave; l&atilde;ng mạn nhất ở Huế, c&oacute; lẽ m&igrave;nh sẽ chọn nơi n&agrave;y. Trước khung cảnh n&uacute;i rừng h&ugrave;ng vĩ lại c&oacute; một d&ograve;ng s&ocirc;ng thơ mộng l&atilde;ng đ&atilde;ng tr&ocirc;i qua. S&ocirc;ng Hương hiền h&ograve;a, lặng lẽ nhưng lại khiến l&ograve;ng người n&ocirc;n nao đến lạ. M&igrave;nh th&iacute;ch nhất cảm gi&aacute;c được ngắm s&ocirc;ng Hương thế n&agrave;y, một cảm gi&aacute;c b&igrave;nh y&ecirc;n kh&oacute; tả.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-17.jpg\" style=\"height:668px; width:960px\" /></p>\r\n\r\n<p><em>Khu rừng th&ocirc;ng tr&ecirc;n đồi Vọng Cảnh.</em></p>\r\n\r\n<p>Chia tay Huế khi trời cũng đ&atilde; xế chiều, m&igrave;nh trả ph&ograve;ng hostel đ&oacute;n xe về lại Đồng Nai, kết th&uacute;c 2 ng&agrave;y b&igrave;nh y&ecirc;n, &yacute; nghĩa ở th&agrave;nh phố Huế. Kết th&uacute;c chuyến đi, m&igrave;nh lại cảm thấy y&ecirc;u Huế hơn, y&ecirc;u&nbsp;n&eacute;t b&igrave;nh dị, y&ecirc;u cuộc sống nhẹ nh&agrave;ng, y&ecirc;u c&aacute;i kh&ocirc;ng kh&iacute; trong l&agrave;nh v&agrave; y&ecirc;u những c&ocirc;ng tr&igrave;nh lịch sử nơi đ&acirc;y. Những ng&agrave;y mệt mỏi với c&ocirc;ng việc, căng thẳng với cuộc sống, h&atilde;y vứt bỏ tất cả v&agrave; đến Huế để cảm nhận cuộc sống nhẹ nh&agrave;ng v&agrave; thanh b&igrave;nh đến chừng n&agrave;o.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-20.jpg\" style=\"height:617px; width:960px\" /></p>\r\n\r\n<p><em>Từ đồi Vọng Cảnh ngắm cảnh s&ocirc;ng Hương để cảm nhận sự b&igrave;nh y&ecirc;n, tĩnh lặng.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/dulich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao-23102018-18.jpg\" style=\"height:668px; width:960px\" /></p>\r\n\r\n<h2><strong>* Tổng kết chuyến đi:</strong></h2>\r\n\r\n<p>- V&eacute; xe kh&aacute;ch: 500.000Đ/lượt</p>\r\n\r\n<p>- Tiền hostel: 117.000Đ/ng&agrave;y</p>\r\n\r\n<p>- Tiền thu&ecirc; xe m&aacute;y: 120.000Đ/ng&agrave;y</p>\r\n\r\n<p>- V&eacute; tham quan Đại Nội: 150.000Đ/người</p>\r\n\r\n<p>- V&eacute; tham quan lăng Khải Định: 100.000Đ/người</p>\r\n\r\n<p>=&gt;&gt; Tiền ăn uống, mua qu&agrave; lưu niệm linh tinh tổng cộng chi ph&iacute; cho chuyến đi của m&igrave;nh gần 2 triệu.</p>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\">&nbsp;</div>', '2019-12-19 00:51:39', 1, 4, 'du-lich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao.1576716698.jpg', 'du-lich-hue-chua-di-chua-biet-hue-dep-va-mong-mo-co-nao.228.html');
INSERT INTO `tb_news` (`id`, `tieude`, `tomtat`, `noidung`, `ngaydang`, `id_user`, `id_diemden`, `avatar`, `slug`) VALUES
(13, 'Hành trình theo chân “trai đẹp” 4 ngày vi vu Cát Bà – Vịnh Lan Hạ – Vịnh Hạ Long – Yên Tử', 'Một hành trình ý nghĩa, một chuyến du lịch tự túc trọn vẹn ở vùng biển phía Bắc, chiêm ngưỡng cảnh đẹp của kì quan thiên nhiên Hạ Long, khám phá vịnh Lan Hạ, đảo Cát Bà, đỉnh thiêng Yên Tử của chàng trai trẻ.', '<h2 style=\"text-align: center;\"><strong><a id=\"type1\" name=\"type1\">Ng&agrave;y 1:&nbsp;H&agrave;nh tr&igrave;nh đến với đảo C&aacute;t B&agrave;</a></strong></h2>\r\n\r\n<p><em>Xuất ph&aacute;t từ S&agrave;i G&ograve;n v&agrave; l&ecirc;n m&aacute;y bay kh&aacute; sớm, 6h l&agrave; m&aacute;y bay cất c&aacute;nh, đ&uacute;ng 8h đ&aacute;p s&acirc;n bay C&aacute;t Bi ở Hải Ph&ograve;ng. M&igrave;nh thu&ecirc; xe m&aacute;y ở s&acirc;n bay lu&ocirc;n để tiện di chuyển đến c&aacute;c điểm ăn uống v&agrave; về kh&aacute;ch sạn. Nghe n&oacute;i m&oacute;n chả v&agrave; quẩy ở qu&aacute;n Phở Tr&acirc;u ngon nổi tiếng n&ecirc;n m&igrave;nh gh&eacute; qua thử. Theo m&igrave;nh th&igrave; m&oacute;n n&agrave;y ổn, kh&ocirc;ng giống với m&oacute;n quẩy ở S&agrave;i G&ograve;n đ&acirc;u nh&eacute;! Theo lịch tr&igrave;nh ban đầu, m&igrave;nh sẽ tham quan C&aacute;t B&agrave; n&ecirc;n đi xe ra thẳng bến ph&agrave;, đến nơi th&igrave; trời cũng đ&atilde; trưa.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-1.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>Đường đi tr&ecirc;n&nbsp;đảo C&aacute;t B&agrave;.&nbsp;</em></p>\r\n\r\n<p>Đến C&aacute;t B&agrave; m&igrave;nh về thẳng Le Point Bungalow Hostel nhận ph&ograve;ng, nghỉ ngơi một t&iacute; rồi ăn trưa lu&ocirc;n. M&igrave;nh ăn trưa ở hostel v&igrave; nghe n&oacute;i sẽ được giảm 10% ho&aacute; đơn d&agrave;nh cho kh&aacute;ch lưu tr&uacute; ở đ&oacute;. Đồ ăn của hostel chủ yếu l&agrave; m&oacute;n T&acirc;y v&igrave; hầu hết kh&aacute;ch ở đ&acirc;y đều l&agrave; người T&acirc;y, mấy m&oacute;n ăn n&oacute;i chung cũng được. Điều m&agrave; m&igrave;nh ấn tượng nhất vẫn l&agrave; view của homestay kh&aacute; đẹp, tha hồ sống ảo. Ăn uống no n&ecirc;, nghỉ ngơi một t&iacute; m&igrave;nh ra biển đi dạo, ngắm cảnh, đi bộ xung quanh tham quan v&agrave; kh&ocirc;ng thể thiếu chụp ảnh sống ảo.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-2.jpg\" style=\"height:720px; width:960px\" /><em>B&atilde;i biển của hostel.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-10.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-11.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-12.jpg\" style=\"height:707px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-13.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>View b&ecirc;n ngo&agrave;i hostel.</em></p>\r\n\r\n<p>Đi loanh quanh một v&ograve;ng, m&igrave;nh l&ecirc;n tham quan khu Ph&aacute;o Đ&agrave;i Thần C&ocirc;ng (gi&aacute; v&eacute; 40.000Đ/người), l&ecirc;n đ&oacute; tha hồ tham quan c&aacute;c khu di t&iacute;ch, leo n&uacute;i v&agrave; ngắm cảnh. Leo n&uacute;i mỏi ch&acirc;n rồi m&igrave;nh lại về chợ C&aacute;t B&agrave; ăn tối. Được người d&acirc;n địa phương giới thiệu m&igrave;nh gh&eacute; v&agrave;o qu&aacute;n chuy&ecirc;n c&aacute;c m&oacute;n vịt, ăn cũng ngon, gi&aacute; th&igrave; theo m&igrave;nh cũng ổn. V&igrave; ở đảo buổi tối cũng kh&ocirc;ng c&oacute; nhiều khu vực vui chơi n&ecirc;n c&oacute; thể sẽ dễ ch&aacute;n, m&igrave;nh đến SkyBar v&igrave; nghe n&oacute;i ở đ&acirc;y vui chơi thoải m&aacute;i nhất, bia S&agrave;i G&ograve;n chỉ c&oacute; 12.000Đ/chai, nh&acirc;m nhi đến 9h l&agrave; m&igrave;nh về hostel để nghỉ ngơi.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-3-1.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-4.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-5.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-6.jpg\" style=\"height:960px; width:670px\" /></p>\r\n\r\n<p><em>Khu vực ph&aacute;o đ&agrave;i Thần C&ocirc;ng.</em></p>\r\n\r\n<h2><a id=\"type2\" name=\"type2\"><strong>N</strong><strong>g&agrave;y</strong><strong>&nbsp;2:&nbsp;</strong><strong>Kh&aacute;m ph&aacute;&nbsp;</strong><strong>Vịnh Lan Hạ, tham quan c&aacute;c hang động</strong></a></h2>\r\n\r\n<p>S&aacute;ng m&igrave;nh dậy thật sớm, ăn s&aacute;ng tại hostel lu&ocirc;n rồi ra bến B&egrave;o thu&ecirc; t&agrave;u để tham quan vịnh Lan Hạ, ở đ&acirc;y m&igrave;nh c&oacute; ch&egrave;o thuyền Kayak (chi ph&iacute; l&agrave; 200.000Đ/người, 40.000Đ ph&iacute; bảo vệ m&ocirc;i trường). Phải c&ocirc;ng nhận l&agrave; vịnh Lan Hạ rất đẹp, theo c&aacute;i nh&igrave;n chủ quan của m&igrave;nh th&igrave; m&igrave;nh thấy ở đ&acirc;y đẹp hơn vịnh Hạ Long, phong cảnh hữu t&igrave;nh, n&uacute;i non h&ugrave;ng vĩ, ấn tượng lắm.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-16.jpg\" style=\"height:960px; width:719px\" /></p>\r\n\r\n<p><em>Ngồi thuyền ra vịnh Lan Hạ.</em></p>\r\n\r\n<p>Lần đầu được ch&egrave;o thuyền Kayak nhưng m&igrave;nh thấy cũng dễ ch&egrave;o lắm, cảm gi&aacute;c th&uacute; vị v&ocirc; c&ugrave;ng. Theo kinh nghiệm của m&igrave;nh, trước khi ch&egrave;o thuyền giữa biển, bạn nhớ để &yacute; thuyền m&agrave; m&igrave;nh đi v&agrave; đừng đi qu&aacute; xa nơi thuyền cập bến v&igrave; khi quay lại sẽ mất nhiều thời gian m&agrave; lại c&ograve;n mệt nữa, hơn nữa cũng dễ bị lạc. T&ugrave;y theo thỏa thuận với chủ thuyền m&agrave; bạn c&oacute; thể ch&egrave;o thuyền kayak từ 3 &ndash; 4 tiếng, tha hồ m&agrave; kh&aacute;m ph&aacute;, nhớ mặc &aacute;o phao để đảm bảo an to&agrave;n nh&eacute;!</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-14.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-15.jpg\" style=\"height:720px; width:960px\" /><em>Ch&egrave;o thuyền Kayak - trải nghiệm kh&oacute; qu&ecirc;n ở Lan Hạ.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-17.jpg\" style=\"height:720px; width:960px\" /><em>Cảnh đẹp vịnh Lan Hạ phải n&oacute;i l&agrave; tuyệt vời.</em></p>\r\n\r\n<p>11h m&igrave;nh quay trở lại thuyền về lại hostel để trả ph&ograve;ng, sau đ&oacute; ăn trưa. Ăn uống no n&ecirc;, m&igrave;nh di chuyển đến vườn quốc gia C&aacute;t B&agrave; tham quan c&aacute;c hang động. Ở đ&acirc;y c&oacute; 4 động, th&igrave; m&igrave;nh chỉ đi c&oacute; 2 hang th&ocirc;i (động Qu&acirc;n Y v&agrave; động Hoa Cương &ndash; ph&iacute; tham quan từ 30.000Đ &ndash; 40.000Đ/người). Theo m&igrave;nh th&igrave; động Hoa Cương đẹp hơn, mấy khối thạch nhũ đủ h&igrave;nh d&aacute;ng thật sự một thế giới k&igrave; b&iacute;. Chia tay với mấy hang động ở C&aacute;t B&agrave;, m&igrave;nh đi ph&agrave; di chuyển đến Tuần Ch&acirc;u (v&eacute; ph&agrave; 80.000Đ/người, nếu bạn c&oacute; đi th&ecirc;m xe m&aacute;y l&agrave; 100.000Đ/cả người v&agrave; xe). Ph&agrave; c&oacute; đi ngang qua vịnh Hạ Long, bạn c&oacute; thể ngắm cảnh lu&ocirc;n nh&eacute;!</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-20.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Động Hoa Cương.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-19.jpg\" style=\"height:720px; width:960px\" /><em>Từ động Hoa Cương đi theo cầu gỗ n&agrave;y khoảng 1km nữa l&agrave; đến đền Th&aacute;nh Mẫu.</em></p>\r\n\r\n<p>Đến&nbsp;<a href=\"https://www.vietnambooking.com/du-lich/tour-du-lich/du-lich-ha-long.html\"><strong>du lịch Hạ Long</strong></a>, m&igrave;nh di chuyển đến kh&aacute;ch sạn để nhận ph&ograve;ng v&agrave; nghỉ ngơi lu&ocirc;n. Kh&aacute;ch sạn m&igrave;nh thu&ecirc; c&aacute;ch chợ Hạ Long khoảng 10km, nhưng do ở tr&ecirc;n một chung cư n&ecirc;n view đẹp, tha hồ ngắm cảnh. Sau khi nghỉ ngơi được một t&iacute;, m&igrave;nh đi ăn tối ở chợ Hạ Long, chủ yếu l&agrave; ăn hải sản, đồ ăn ở đ&acirc;y cũng phong ph&uacute; lắm (b&aacute;nh đa, chả mực, ch&egrave;&hellip;) cũng kh&ocirc;ng hề chặt ch&eacute;m n&ecirc;n cảm thấy rất h&agrave;i l&ograve;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-18.jpg\" style=\"height:720px; width:960px\" /><em>Ho&agrave;ng h&ocirc;n tr&ecirc;n vịnh Hạ Long.</em></p>\r\n\r\n<h2><a id=\"type3\" name=\"type3\"><strong>Ng&agrave;y 3: Tham Quan Vịnh Hạ Long, Bảo T&agrave;ng Quảng Ninh v&agrave; Sun World</strong></a></h2>\r\n\r\n<p>Buổi s&aacute;ng đầu ti&ecirc;n ở vịnh Hạ Long, m&igrave;nh cũng dậy sớm để ăn s&aacute;ng, được giới thiệu m&oacute;n b&uacute;n hải sản Đức Chung ngon n&ecirc;n m&igrave;nh cũng đến thử, phải c&ocirc;ng nhận l&agrave; tuyệt vời. Ăn uống xong, m&igrave;nh ra bến cảng Tuần Ch&acirc;u thu&ecirc; thuyền tham quan vịnh Hạ Long, ở đ&acirc;y c&oacute; rất nhiều thuyền v&agrave; cũng c&oacute; nhiều gi&aacute;, bạn c&oacute; thể hỏi gi&aacute; trước v&agrave; thỏa thuận gi&aacute; với chủ t&agrave;u. Nếu đi gh&eacute;p c&ugrave;ng những kh&aacute;ch kh&aacute;c gi&aacute; sẽ rẻ hơn, khoảng 200.000Đ/người, m&igrave;nh thu&ecirc; thuyền ri&ecirc;ng với gi&aacute; 1.600.000Đ/4 người, thời gian tham quan từ 3 &ndash; 4 tiếng. Ngo&agrave;i ra th&igrave; v&agrave;o cổng khu du lịch Tuần Ch&acirc;u cũng tốn th&ecirc;m 250.000Đ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-24.jpg\" style=\"height:720px; width:960px\" /><em>Bến t&agrave;u Tuần Ch&acirc;u.</em></p>\r\n\r\n<p>Thuyền ở Hạ Long chạy kh&aacute; chậm, bạn tha hồ ngắm cảnh, chụp ảnh sống ảo c&aacute;c kiểu. V&agrave; dĩ nhi&ecirc;n, thuyền sẽ chở bạn đi ngang qua c&aacute;c h&ograve;n nổi tiếng ở Hạ Long như: h&ograve;n Trống M&aacute;i, h&ograve;n Đỉnh Hương&hellip; cảnh đẹp tuyệt vời, bạn tha hồ chụp ảnh nh&eacute;! Kết th&uacute;c h&agrave;nh tr&igrave;nh l&ecirc;n đ&ecirc;nh tr&ecirc;n biển ở Hạ Long, m&igrave;nh quay lại bến thuyền Tuần Ch&acirc;u rồi đi ăn trưa ở chợ Hạ Long. M&igrave;nh c&oacute; gh&eacute; qua bảo t&agrave;ng Quảng Ninh để tham quan nhưng v&igrave; đi đ&uacute;ng ng&agrave;y thứ 2, ở đ&acirc;y đ&oacute;ng cửa n&ecirc;n chỉ đi tham quan chụp ảnh khu vực b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-21-1.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-22.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><em>L&ecirc;nh đ&ecirc;nh giữa biển, ngắm cảnh Hạ Long.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-25.jpg\" style=\"height:720px; width:960px\" /><em>Vịnh Hạ Long.</em></p>\r\n\r\n<p>Buổi chiều, m&igrave;nh đến khu Sun World để tham quan, vui chơi, m&igrave;nh mua v&eacute; combo tham quan 2 khu Dragon Park v&agrave; Sun Wheel, gi&aacute; 400.000Đ/người. Ở đ&acirc;y c&oacute; kh&aacute; nhiều tr&ograve; chơi mạo hiểm, phải đảm bảo bạn kh&ocirc;ng phải l&agrave; người yếu tim th&igrave; mới chơi nha. Đến khu Sun Wheel nhất định phải xem m&uacute;a rối nước. Chơi thả ga ở Sun World đến chiều tối, m&igrave;nh đi ăn b&uacute;n sườn ở kh&aacute; gần kh&aacute;ch sạn rồi về nghỉ ngơi, dưỡng sức để ng&agrave;y h&ocirc;m sau đi Y&ecirc;n Tử. Đ&aacute;ng l&iacute; ra m&igrave;nh c&ograve;n dự định đi n&uacute;i b&agrave;i Thơ nhưng v&igrave; kh&ocirc;ng c&ograve;n thời gian nữa n&ecirc;n đ&agrave;nh bỏ lỡ vậy.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-29.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-30.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-31.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-32.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-33.jpg\" style=\"height:720px; width:960px\" /><em>Khu Sun World.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-27.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><em>Bảo t&agrave;ng Quảng Ninh.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-28.jpg\" style=\"height:720px; width:960px\" /><em>Ph&iacute;a sau bảo t&agrave;ng.</em></p>\r\n\r\n<h2><a id=\"type4\" name=\"type4\"><strong>Ng&agrave;y 4:&nbsp;</strong><strong>Viếng ch&ugrave;a</strong><strong>&nbsp;Y&ecirc;n Tử</strong></a></h2>\r\n\r\n<p>Đoạn đường từ Hạ Long đến Y&ecirc;n Tử khoảng 40km n&ecirc;n m&igrave;nh quyết định dậy sớm để l&ecirc;n đường. Tr&ecirc;n đường đi m&igrave;nh cũng c&oacute; gh&eacute; qua tham quan ch&ugrave;a Ba V&agrave;ng, ch&ugrave;a n&agrave;y cũng lớn v&agrave; kh&aacute; đẹp. V&igrave; được nhiều người m&aacute;ch leo n&uacute;i Y&ecirc;n Tử sẽ mệt lắm n&ecirc;n m&igrave;nh mua th&ecirc;m nước v&agrave; thức ăn nhẹ để l&oacute;t dạ, tr&aacute;nh t&igrave;nh trạng chưa l&ecirc;n đến nơi m&agrave; đ&atilde; hết hơi. Tr&ecirc;n đường leo l&ecirc;n đỉnh n&uacute;i cũng c&oacute; trạm dừng ch&acirc;n, ở đ&acirc;y người ta cũng c&oacute; b&aacute;n nước uống v&agrave; thức ăn nhẹ v&agrave; gi&aacute; hơi cao t&iacute;. Nếu kh&ocirc;ng muốn leo n&uacute;i, bạn c&oacute; thể chọn đi c&aacute;p treo, 1 chiều l&agrave; 200.000Đ/người, khứ hồi l&agrave; 300.000Đ/người.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-34.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-35.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-36.jpg\" style=\"height:720px; width:960px\" /><em>Ch&ugrave;a Ba V&agrave;ng.</em></p>\r\n\r\n<p>Nếu leo bộ l&ecirc;n n&uacute;i v&agrave; đi bộ xuống nữa c&oacute; thể mất hơn 5 tiếng, d&ugrave; c&oacute; hơi mệt nhưng đến Y&ecirc;n Tử m&agrave; kh&ocirc;ng đi bộ l&ecirc;n n&uacute;i th&igrave; cũng kh&ocirc;ng c&ograve;n g&igrave; th&uacute; vị. M&igrave;nh l&ecirc;n đến nơi cao nhất l&agrave; ch&ugrave;a Đồng, ng&ocirc;i ch&ugrave;a cao nhất nước ta. Từ tr&ecirc;n đỉnh n&uacute;i ngắm cảnh m&acirc;y tr&ocirc;i bồng bềnh, sương m&ugrave; khắp nơi cũng th&uacute; vị kh&ocirc;ng thua g&igrave; với cảnh đẹp ở Fansipan đ&acirc;u nha. Kết th&uacute;c h&agrave;nh tr&igrave;nh leo n&uacute;i &ldquo;vật v&atilde;&rdquo; ở Y&ecirc;n Tử nhưng b&ugrave; lại cảm thấy t&acirc;m hồn thư th&aacute;i, nhẹ nh&agrave;ng hơn rất nhiều. M&igrave;nh quay trở lại Hải Ph&ograve;ng để về S&agrave;i G&ograve;n, tr&ecirc;n đường đi c&oacute; gh&eacute; ngang Thiền Viện Tr&uacute;c L&acirc;m.</p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-38.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-39.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-40.jpg\" style=\"height:720px; width:960px\" /><em>Đỉnh thi&ecirc;ng Y&ecirc;n Tử.</em></p>\r\n\r\n<p><img alt=\"\" src=\"https://blog.vietnambooking.com/wp-content/uploads/2018/10/hanh-trinh-theo-chan-trai-dep-4ngay-vivu-cat-ba-lan-ha-ha-long-yen-tu-26102018-41.jpg\" style=\"height:720px; width:960px\" /><em>Ch&ugrave;a Đồng ch&igrave;m&nbsp;trong sương m&ugrave;.</em></p>\r\n\r\n<p>Trước khi ra s&acirc;n bay m&igrave;nh cũng kh&ocirc;ng qu&ecirc;n thưởng thức b&aacute;nh đa cua &ndash; đặc sản Hải Ph&ograve;ng, b&aacute;nh m&igrave; cay, dừa dầm rồi mua đặc sản về tặng người th&acirc;n. Vậy l&agrave; m&igrave;nh đ&atilde; kết th&uacute;c h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; vịnh Lan Hạ, C&aacute;t B&agrave;, Hạ Long, Y&ecirc;n Tử 4 ng&agrave;y đầy th&uacute; vị v&agrave; hi vọng một ng&agrave;y n&agrave;o đ&oacute; sẽ trở lại nơi n&agrave;y lần nữa.</p>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\">&nbsp;</div>', '2019-12-19 11:07:12', 1, 2, 'hanh-trinh-theo-chan-trai-dep-4-ngay-vi-vu-cat-ba-vinh-lan-ha-vinh-ha-long-yen-tu.1576716823.jpg', 'hanh-trinh-theo-chan-trai-dep-4-ngay-vi-vu-cat-ba-vinh-lan-ha-vinh-ha-long-yen-tu.286.html');

